import React, { createContext, useContext, useState, useEffect } from 'react';
import { Session } from '@supabase/supabase-js';
import { USER_ROLES } from '../utils/constants/roles';

// Tipos
interface User {
  id: string;
  email: string;
  name?: string;
  role: string;
  entityId?: string; // ID del asesor o empresa asociada
}

interface AuthContextType {
  user: User | null;
  session: Session | null;
  loading: boolean;
  signIn: (email: string, password: string) => Promise<{
    error: any | null;
    data: any | null;
  }>;
  signInWithCode: (accessCode: string, userType: string) => Promise<{
    error: any | null;
    data: any | null;
  }>;
  signOut: () => Promise<void>;
  setUser: React.Dispatch<React.SetStateAction<User | null>>;
}

const USER_STORAGE_KEY = 'crm_user';
const SESSION_EXPIRY_KEY = 'crm_session_expiry';
const SESSION_DURATION = 24 * 60 * 60 * 1000; // 24 horas en milisegundos

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [session, setSession] = useState<Session | null>(null);
  const [loading, setLoading] = useState(true);

  // Función para establecer la fecha de expiración de la sesión
  const setSessionExpiry = () => {
    const expiryTime = Date.now() + SESSION_DURATION;
    localStorage.setItem(SESSION_EXPIRY_KEY, expiryTime.toString());
    return expiryTime;
  };

  // Función para verificar si la sesión ha expirado
  const hasSessionExpired = (): boolean => {
    const expiryTime = localStorage.getItem(SESSION_EXPIRY_KEY);
    if (!expiryTime) return true;

    return Date.now() > parseInt(expiryTime);
  };

  // Verificación de sesión al iniciar y configurar event listeners
  useEffect(() => {
    const checkStoredUser = () => {
      try {
        setLoading(true);

        // Verificar si hay un usuario almacenado
        const storedUser = localStorage.getItem(USER_STORAGE_KEY);

        if (storedUser) {
          // Verificar si la sesión ha expirado
          if (hasSessionExpired()) {
            console.log('La sesión ha expirado, cerrando sesión');
            // Limpiar localStorage si la sesión expiró
            localStorage.removeItem(USER_STORAGE_KEY);
            localStorage.removeItem(SESSION_EXPIRY_KEY);
            setUser(null);
          } else {
            // Renovar la expiración de la sesión
            setSessionExpiry();
            setUser(JSON.parse(storedUser));
          }
        }
      } catch (error) {
        console.error('Error checking stored user:', error);
        localStorage.removeItem(USER_STORAGE_KEY);
        localStorage.removeItem(SESSION_EXPIRY_KEY);
      } finally {
        setLoading(false);
      }
    };

    // Verificar al inicio
    checkStoredUser();

    // Configurar event listener para storage changes (sesiones múltiples)
    const handleStorageChange = (e: StorageEvent) => {
      if (e.key === USER_STORAGE_KEY) {
        if (e.newValue) {
          setUser(JSON.parse(e.newValue));
        } else {
          setUser(null);
        }
      }
    };

    window.addEventListener('storage', handleStorageChange);

    // Renovar sesión periódicamente si el usuario está activo
    const renewalInterval = setInterval(() => {
      if (user) {
        setSessionExpiry(); // Renovar tiempo de expiración
      }
    }, 15 * 60 * 1000); // Renovar cada 15 minutos

    // Limpiar
    return () => {
      window.removeEventListener('storage', handleStorageChange);
      clearInterval(renewalInterval);
    };
  }, [user]);

  const signIn = async (email: string, password: string) => {
    try {
      setLoading(true);

      // Simulación de autenticación
      if (email === 'admin@fincentiva.com' && password === 'admin123') {
        const mockUser: User = {
          id: '1',
          email: 'admin@fincentiva.com',
          name: 'Administrador',
          role: 'SUPERADMIN',
        };

        // Guardar en localStorage para mantener la sesión
        localStorage.setItem(USER_STORAGE_KEY, JSON.stringify(mockUser));
        setSessionExpiry(); // Establecer tiempo de expiración
        setUser(mockUser);

        return { data: { user: mockUser }, error: null };
      }

      return { data: null, error: 'Credenciales inválidas' };
    } catch (error) {
      console.error('Error signing in:', error);
      return { error, data: null };
    } finally {
      setLoading(false);
    }
  };

  const signInWithCode = async (accessCode: string, userType: string) => {
    try {
      setLoading(true);

      // Consultar la base de datos para verificar el access_code
      let queryUrl = 'http://localhost:3100/query';
      let queryBody = {};

      if (userType === USER_ROLES.ADVISOR) {
        queryBody = {
          query: `SELECT * FROM advisors WHERE access_code = '${accessCode}' LIMIT 1`
        };
      } else if (userType === USER_ROLES.COMPANY_ADMIN) {
        queryBody = {
          query: `SELECT * FROM company_admins WHERE access_code = '${accessCode}' LIMIT 1`
        };
      } else {
        return { data: null, error: 'Tipo de usuario no válido' };
      }

      const response = await fetch(queryUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(queryBody),
      });

      const result = await response.json();

      if (result.error) {
        console.error('Database error:', result.error);
        return { data: null, error: 'Error de base de datos' };
      }

      if (result.data && result.data.length > 0) {
        const userData = result.data[0];
        const userInfo: User = {
          id: userData.id,
          email: userData.email,
          name: userData.name,
          role: userType,
          entityId: userType === USER_ROLES.COMPANY_ADMIN ? userData.company_id : userData.id,
        };

        // Guardar en localStorage para mantener la sesión
        localStorage.setItem(USER_STORAGE_KEY, JSON.stringify(userInfo));
        setSessionExpiry(); // Establecer tiempo de expiración
        setUser(userInfo);

        return { data: { user: userInfo }, error: null };
      }

      return { data: null, error: 'Código de acceso inválido' };
    } catch (error) {
      console.error('Error signing in with code:', error);
      // Ensure returned error is always a string
      return { 
        error: error instanceof Error ? error.message : 'Error al iniciar sesión', 
        data: null 
      };
    } finally {
      setLoading(false);
    }
  };

  const signOut = async () => {
    try {
      // Eliminar datos de sesión
      localStorage.removeItem(USER_STORAGE_KEY);
      localStorage.removeItem(SESSION_EXPIRY_KEY);
      setUser(null);
      setSession(null);
    } catch (error) {
      console.error('Error signing out:', error);
    }
  };

  const value = {
    user,
    session,
    loading,
    signIn,
    signInWithCode,
    signOut,
    setUser,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
