import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { PERMISSIONS } from '../utils/constants/permissions';
import { usePermissions } from '../contexts/PermissionsContext';
import MainLayout from '../components/layout/MainLayout';
import applicationService, { Application, APPLICATION_STATUS } from '../services/applicationService';

const Applications: React.FC = () => {
  const { userCan } = usePermissions();
  const [applications, setApplications] = useState<Application[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState<string>('all');

  useEffect(() => {
    const fetchApplications = async () => {
      setIsLoading(true);

      try {
        // Usamos el servicio para obtener las solicitudes
        const filters: any = {};

        // Aplicar filtro de estado si no es 'all'
        if (statusFilter !== 'all') {
          filters.status = statusFilter;
        }

        const data = await applicationService.getAllApplications(filters);
        setApplications(data);
      } catch (error) {
        console.error('Error fetching applications:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchApplications();
  }, [statusFilter]);

  const getStatusClass = (status: string) => {
    switch (status) {
      case APPLICATION_STATUS.PENDING:
        return 'badge-warning';
      case APPLICATION_STATUS.REVIEW:
        return 'badge-info';
      case APPLICATION_STATUS.APPROVED:
        return 'badge-success';
      case APPLICATION_STATUS.REJECTED:
        return 'badge-error';
      case APPLICATION_STATUS.COMPLETED:
        return 'badge-primary';
      case APPLICATION_STATUS.CANCELLED:
        return 'badge-ghost';
      default:
        return 'badge-ghost';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case APPLICATION_STATUS.PENDING:
        return 'Pendiente';
      case APPLICATION_STATUS.REVIEW:
        return 'En Revisión';
      case APPLICATION_STATUS.APPROVED:
        return 'Aprobada';
      case APPLICATION_STATUS.REJECTED:
        return 'Rechazada';
      case APPLICATION_STATUS.COMPLETED:
        return 'Completada';
      case APPLICATION_STATUS.CANCELLED:
        return 'Cancelada';
      default:
        return status;
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('es-ES', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-CL', {
      style: 'currency',
      currency: 'CLP',
    }).format(amount);
  };

  const filteredApplications = applications.filter((app) => {
    // Ya filtramos por estado en la llamada a la API, solo necesitamos filtrar por búsqueda aquí
    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      return (
        app.applicant_name.toLowerCase().includes(query) ||
        app.email.toLowerCase().includes(query) ||
        app.phone.toLowerCase().includes(query)
      );
    }

    return true;
  });

  if (!userCan(PERMISSIONS.VIEW_APPLICATIONS)) {
    return (
      <MainLayout>
        <div className="flex items-center justify-center h-96">
          <div className="text-center">
            <h2 className="text-2xl font-bold text-gray-700">Acceso Restringido</h2>
            <p className="text-gray-500 mt-2">No tienes permisos para ver esta página.</p>
          </div>
        </div>
      </MainLayout>
    );
  }

  return (
    <MainLayout>
      <div className="p-6">
        <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-6">
          <h1 className="text-2xl font-bold mb-4 md:mb-0">Solicitudes de Crédito</h1>

          {userCan(PERMISSIONS.CREATE_APPLICATION) && (
            <Link to="/applications/new" className="btn btn-primary">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M10 3a1 1 0 011 1v5h5a1 1 0 110 2h-5v5a1 1 0 11-2 0v-5H4a1 1 0 110-2h5V4a1 1 0 011-1z" clipRule="evenodd" />
              </svg>
              Nueva Solicitud
            </Link>
          )}
        </div>

        <div className="bg-base-100 shadow-xl rounded-box p-6 mb-6">
          <div className="flex flex-col md:flex-row gap-4 mb-6">
            <div className="form-control flex-1">
              <div className="input-group">
                <input
                  type="text"
                  placeholder="Buscar por nombre, email o teléfono"
                  className="input input-bordered w-full"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
                <button className="btn btn-square">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                  </svg>
                </button>
              </div>
            </div>

            <select
              className="select select-bordered"
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
            >
              <option value="all">Todos los estados</option>
              <option value={APPLICATION_STATUS.PENDING}>Pendiente</option>
              <option value={APPLICATION_STATUS.REVIEW}>En Revisión</option>
              <option value={APPLICATION_STATUS.APPROVED}>Aprobada</option>
              <option value={APPLICATION_STATUS.REJECTED}>Rechazada</option>
              <option value={APPLICATION_STATUS.COMPLETED}>Completada</option>
              <option value={APPLICATION_STATUS.CANCELLED}>Cancelada</option>
            </select>
          </div>

          {isLoading ? (
            <div className="flex justify-center items-center h-64">
              <span className="loading loading-spinner loading-lg"></span>
            </div>
          ) : filteredApplications.length === 0 ? (
            <div className="text-center py-12">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-12 w-12 mx-auto text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
              </svg>
              <h3 className="mt-4 text-xl font-medium text-gray-600">No se encontraron solicitudes</h3>
              <p className="mt-2 text-gray-500">Intenta cambiar los filtros de búsqueda</p>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="table table-zebra w-full">
                <thead>
                  <tr>
                    <th>Fecha</th>
                    <th>Solicitante</th>
                    <th>Contacto</th>
                    <th>Estado</th>
                    <th>Monto</th>
                    <th>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredApplications.map((app) => (
                    <tr key={app.id}>
                      <td>{app.created_at ? formatDate(app.created_at) : '-'}</td>
                      <td>{app.applicant_name}</td>
                      <td>
                        <div>{app.email}</div>
                        <div className="text-sm opacity-70">{app.phone}</div>
                      </td>
                      <td>
                        <div className={`badge ${getStatusClass(app.status)}`}>
                          {getStatusText(app.status)}
                        </div>
                      </td>
                      <td>{formatCurrency(app.amount)}</td>
                      <td>
                        <div className="flex gap-2">
                          <Link to={`/applications/${app.id}`} className="btn btn-xs btn-outline">
                            Ver
                          </Link>
                          {userCan(PERMISSIONS.EDIT_APPLICATION) && (
                            <Link to={`/applications/${app.id}/edit`} className="btn btn-xs btn-outline btn-primary">
                              Editar
                            </Link>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </MainLayout>
  );
};

export default Applications;
