          console.error('Invalid document data', document);
          throw new Error('Datos de documento inválidos: se requiere archivo y nombre');
        }

        // Create a document upload request compatible with the document service
        const uploadParams = {
          file: document.file,
          category: document.category,
          documentName: document.name,
          description: document.description,
          userId: userId,
          client_id: clientId,
          authClient: authClient
        };

        // Subir el documento
        return await documentService.uploadDocument(uploadParams);
      } catch (docError) {
        // Manejo de errores específicos de permisos RLS
        if (docError.message && (docError.message.includes('permission denied') || docError.message.includes('not authorized') || docError.message.includes('row level security'))) {
