import { supabase, getAuthenticatedClient } from "../lib/supabaseClient";
import { TABLES } from "../utils/constants/tables";
import * as documentService from './documentService';
import { Client, ClientDocument } from "../types/client";
import { DocumentUpload, Document } from "../types/document";
import { 
  ErrorType, 
  createAppError, 
  logError, 
  showErrorToast, 
  handleApiError 
} from '../utils/errorHandling';
import { parseNumericString } from '../utils/numberFormatting';

// Re-exportamos las interfaces para mantener compatibilidad
export type { Client, ClientDocument };

export interface ClientFilter {
  searchQuery?: string;
  advisor_id?: string;
  company_id?: string;
  dateFrom?: string;
  dateTo?: string;
  page?: number;
  pageSize?: number;
}

const USERS_TABLE = 'users';

// Helper function to convert ClientDocument to DocumentUpload
const convertClientDocumentToUpload = (
  document: ClientDocument, 
  userId: string
): documentService.DocumentUpload => {
  return {
    file: document.file,
    category: document.category,
    documentName: document.name,
    description: document.description,
    userId: userId
  };
};

const mapUserToClient = (userData: any): Client => {
  if (!userData) {
    console.error('Error: userData is undefined in mapUserToClient');
    throw new Error('No se pudo procesar la información del cliente, los datos son inválidos');
  }

  const fullName = userData.name || [
    userData.first_name,
    userData.paternal_surname,
    userData.maternal_surname
  ].filter(Boolean).join(' ');

  return {
