import React, { createContext, useContext, useState, useEffect } from 'react';
import { Session } from '@supabase/supabase-js';
import { USER_ROLES } from '../utils/constants/roles';

// Tipos
interface User {
  id: string;
  email: string;
  name?: string;
  role: string;
  entityId?: string; // ID del asesor o empresa asociada
}

interface AuthContextType {
  user: User | null;
  session: Session | null;
  loading: boolean;
  signIn: (email: string, password: string) => Promise<{
    error: any | null;
    data: any | null;
  }>;
  signInWithCode: (accessCode: string, userType: string) => Promise<{
    error: any | null;
    data: any | null;
  }>;
  signOut: () => Promise<void>;
  setUser: React.Dispatch<React.SetStateAction<User | null>>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [session, setSession] = useState<Session | null>(null);
  const [loading, setLoading] = useState(true);

  // Verificación de sesión al iniciar
  useEffect(() => {
    const checkStoredUser = () => {
      try {
        const storedUser = localStorage.getItem('crm_user');
        if (storedUser) {
          const parsedUser = JSON.parse(storedUser);
          console.log("Loaded user from storage:", parsedUser);

          // Validar que el usuario tiene los campos requeridos
          if (parsedUser && parsedUser.id && parsedUser.email && parsedUser.role) {
            setUser(parsedUser);
          } else {
            console.warn("Stored user data is invalid, clearing session");
            localStorage.removeItem('crm_user');
          }
        }
      } catch (error) {
        console.error('Error checking stored user:', error);
        // Si hay error al parsear, limpiar la sesión
        localStorage.removeItem('crm_user');
      } finally {
        setLoading(false);
      }
    };

    checkStoredUser();
  }, []);

  const signIn = async (email: string, password: string) => {
    try {
      setLoading(true);

      // Simulación de autenticación
      if (email === 'admin@fincentiva.com' && password === 'admin123') {
        const mockUser: User = {
          id: '1',
          email: 'admin@fincentiva.com',
          name: 'Administrador',
          role: 'SUPERADMIN',
        };

        // Guardar en localStorage para mantener la sesión
        localStorage.setItem('crm_user', JSON.stringify(mockUser));
        setUser(mockUser);

        return { data: { user: mockUser }, error: null };
      }

      return { data: null, error: 'Credenciales inválidas' };
    } catch (error) {
      console.error('Error signing in:', error);
      return { error, data: null };
    } finally {
      setLoading(false);
    }
  };

  const signInWithCode = async (accessCode: string, userType: string) => {
    try {
      setLoading(true);
      console.log(`Attempting to sign in with code: ${accessCode}, type: ${userType}`);

      if (!accessCode || accessCode.trim() === '') {
        console.error('Access code is empty');
        return { data: null, error: 'Por favor, ingrese un código de acceso' };
      }

      // Consultar la base de datos para verificar el access_code
      let queryUrl = 'http://localhost:3100/query';
      let queryBody = {};

      if (userType === USER_ROLES.ADVISOR) {
        queryBody = {
          query: `SELECT * FROM advisors WHERE access_code = '${accessCode}' LIMIT 1`
        };
      } else if (userType === USER_ROLES.COMPANY_ADMIN) {
        queryBody = {
          query: `SELECT * FROM company_admins WHERE access_code = '${accessCode}' LIMIT 1`
        };
      } else {
        console.error(`Invalid user type: ${userType}`);
        return { data: null, error: 'Tipo de usuario no válido' };
      }

      console.log("Sending auth query:", queryBody);

      try {
        const response = await fetch(queryUrl, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(queryBody),
        });

        if (!response.ok) {
          const errorText = await response.text();
          console.error(`API Error (${response.status}): ${errorText}`);
          return { data: null, error: `Error de conexión con el servidor (${response.status})` };
        }

        const result = await response.json();
        console.log("Auth result:", result);

        if (result.error) {
          console.error('Database error:', result.error);
          return { data: null, error: `Error de base de datos: ${result.error}` };
        }

        if (!result.data) {
          console.error('No data returned from query');
          return { data: null, error: 'No se recibieron datos de la consulta' };
        }

        if (result.data && result.data.length > 0) {
          const userData = result.data[0];
          console.log("User data retrieved:", userData);

          let entityId: string | undefined;

          if (userType === USER_ROLES.COMPANY_ADMIN) {
            if (!userData.company_id) {
              console.error('Company admin has no company_id');
              return { data: null, error: 'Este administrador no tiene una empresa asignada' };
            }
            entityId = userData.company_id;
          } else { // ADVISOR
            entityId = userData.id;
          }

          const userInfo: User = {
            id: userData.id,
            email: userData.email || 'no-email@example.com',
            name: userData.name || 'Usuario sin nombre',
            role: userType,
            entityId: entityId,
          };

          console.log("Constructed user object:", userInfo);

          // Validar que el objeto tenga toda la información necesaria
          if (!userInfo.id || !userInfo.role || !userInfo.entityId) {
            console.error('Incomplete user information:', userInfo);
            return { data: null, error: 'Información de usuario incompleta' };
          }

          // Guardar en localStorage para mantener la sesión
          localStorage.setItem('crm_user', JSON.stringify(userInfo));
          setUser(userInfo);

          return { data: { user: userInfo }, error: null };
        }

        console.error('No matching user found for access code:', accessCode);
        return { data: null, error: 'Código de acceso inválido' };
      } catch (fetchError) {
        console.error('Network error during authentication:', fetchError);
        return { data: null, error: 'Error de conexión: Verifique que el servidor MCP esté funcionando' };
      }
    } catch (error) {
      console.error('Error signing in with code:', error);
      return { error, data: null };
    } finally {
      setLoading(false);
    }
  };

  const signOut = async () => {
    try {
      // Eliminar datos de sesión
      localStorage.removeItem('crm_user');
      setUser(null);
      setSession(null);
    } catch (error) {
      console.error('Error signing out:', error);
    }
  };

  const value = {
    user,
    session,
    loading,
    signIn,
    signInWithCode,
    signOut,
    setUser,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
