  // Actualizar la función canDragCard para permitir mover tarjetas de status "nuevo" para admin de empresa
  const canDragCard = (app: ApplicationWithApproval): boolean => {
    // Los asesores pueden mover todas las tarjetas excepto las que están en movimiento
    if (isAdvisor()) {
      return !app.isMoving;
    }

    // Para administradores de empresa:
    if (isCompanyAdmin()) {
      // No puede mover tarjetas que ya están en movimiento
      if (app.isMoving) return false;

      // Puede mover tarjetas en estado "nuevo" (new)
      if (app.status === APPLICATION_STATUS.NEW) return true;

      // Puede mover tarjetas en revisión (in_review)
      if (app.status === APPLICATION_STATUS.IN_REVIEW) return true;

      // Puede mover tarjetas a estado aprobado por empresa
      if (app.status === APPLICATION_STATUS.APPROVED) return true;

      // No puede mover tarjetas en estos estados
      if (app.status === APPLICATION_STATUS.REJECTED ||
          app.status === APPLICATION_STATUS.POR_DISPERSAR ||
          app.status === APPLICATION_STATUS.COMPLETED ||
          app.status === APPLICATION_STATUS.EXPIRED ||
          app.status === APPLICATION_STATUS.CANCELLED) {
        return false;
      }

      // Por defecto, permitir movimiento para cualquier otro estado
      return true;
    }

    // Para otros roles, no permitir mover tarjetas
    return false;
  };

  // Modificar handleDrop para asegurar que se cumplan las reglas correctas
  const handleDrop = async (e: React.DragEvent<HTMLDivElement>, newStatus: string) => {
    e.preventDefault();

    // Recuperar datos de la aplicación que se está moviendo
    const dragData = JSON.parse(e.dataTransfer.getData('application'));
    const { application, sourceStatus, index } = dragData;

    // Validar que la aplicación existe
    if (!application || !application.id) {
      console.error('Datos de aplicación inválidos:', dragData);
      return;
    }

    // No hacer nada si no hay cambio de estado
    if (sourceStatus === newStatus) return;

    // Restricciones de movimiento según rol
    if (isCompanyAdmin()) {
      // Administradores de empresa: reglas específicas para ellos

      // No pueden mover a estado rechazado (solo asesores)
      if (newStatus === APPLICATION_STATUS.REJECTED) {
        setErrorMessage('Los administradores de empresa no pueden mover solicitudes a Rechazado');
        return;
      }

      // No pueden mover a "Por Dispersar" directamente, esto debe ser automático
      if (newStatus === APPLICATION_STATUS.POR_DISPERSAR && sourceStatus !== APPLICATION_STATUS.APPROVED) {
        setErrorMessage('Las solicitudes solo pueden pasar a "Por Dispersar" cuando son aprobadas por asesor y empresa');
        return;
      }

      // Reglas para tarjetas en estado "nuevo"
      if (sourceStatus === APPLICATION_STATUS.NEW) {
        // De "Nuevo" solo pueden mover a "En Revisión" o "Aprobado por mi"
        if (newStatus !== APPLICATION_STATUS.IN_REVIEW && newStatus !== APPLICATION_STATUS.APPROVED) {
          setErrorMessage('Las solicitudes nuevas solo pueden moverse a "En Revisión" o "Aprobado por mi"');
          return;
        }
      }

      // Reglas para tarjetas en estado "en revisión"
      if (sourceStatus === APPLICATION_STATUS.IN_REVIEW) {
        // De "En Revisión" solo pueden mover a "Aprobado por mi" o de regreso a "Nuevo"
        if (newStatus !== APPLICATION_STATUS.APPROVED && newStatus !== APPLICATION_STATUS.NEW) {
          setErrorMessage('Las solicitudes en revisión solo pueden moverse a "Aprobado por mi" o "Nuevo"');
          return;
        }
      }

      // Reglas para tarjetas en estado "aprobado"
      if (sourceStatus === APPLICATION_STATUS.APPROVED) {
        // De "Aprobado por mi" solo pueden mover de regreso a "En Revisión" o "Nuevo" 
        if (newStatus !== APPLICATION_STATUS.IN_REVIEW && newStatus !== APPLICATION_STATUS.NEW) {
          setErrorMessage('Las solicitudes aprobadas por empresa solo pueden moverse a "En Revisión" o "Nuevo"');
          return;
        }
      }
    }

    // Actualización optimista del estado local
    const updatedApps = [...appsWithApproval];
    const appIndex = updatedApps.findIndex(app => app.id === application.id);

    if (appIndex !== -1) {
      // Marcar la aplicación como en movimiento y con estado de destino
      updatedApps[appIndex] = {
        ...updatedApps[appIndex],
        isMoving: true,
        targetStatus: newStatus
      };

      setAppsWithApproval(updatedApps);

      // Preparar actualización de aprobación para pasar a "por dispersar"
      if (newStatus === APPLICATION_STATUS.APPROVED) {
        // Actualizar estado de aprobación según el rol del usuario
        if (isAdvisor()) {
          updateLocalApprovalStatus(application.id, { approvedByAdvisor: true });
        } else if (isCompanyAdmin()) {
          updateLocalApprovalStatus(application.id, { approvedByCompany: true });
        }
      }
    }

    try {
      // Llamar a la función para actualizar el estado de la aplicación
      if (onStatusChange) {
        await onStatusChange(application, newStatus);

        // Si todo sale bien, actualizar el estado local
        const finalUpdatedApps = [...appsWithApproval];
        const finalAppIndex = finalUpdatedApps.findIndex(app => app.id === application.id);

        if (finalAppIndex !== -1) {
          // Actualizar el estado y quitar los marcadores de movimiento
          finalUpdatedApps[finalAppIndex] = {
            ...finalUpdatedApps[finalAppIndex],
            status: newStatus,
            isMoving: false,
            targetStatus: undefined
          };

          setAppsWithApproval(finalUpdatedApps);

          // Si ambos aprobadores han aprobado, mover automáticamente a "Por Dispersar"
          const appApprovalStatus = finalUpdatedApps[finalAppIndex].approvalStatus;
          if (appApprovalStatus && 
              appApprovalStatus.approvedByAdvisor && 
              appApprovalStatus.approvedByCompany && 
              newStatus === APPLICATION_STATUS.APPROVED) {

            // Configurar mensaje de transición automática y moverla después de un breve retraso
            setAutoTransitionMessage(`La solicitud de ${application.client_name} ha sido aprobada por ambas partes y avanzará automáticamente a "Por Dispersar"`);

            // Esperar un momento y luego mover automáticamente
            setTimeout(async () => {
              try {
                if (onStatusChange) {
                  await onStatusChange(application, APPLICATION_STATUS.POR_DISPERSAR);

                  // Actualizar el estado local tras el movimiento automático
                  const autoUpdatedApps = [...appsWithApproval];
                  const autoAppIndex = autoUpdatedApps.findIndex(app => app.id === application.id);

                  if (autoAppIndex !== -1) {
                    autoUpdatedApps[autoAppIndex] = {
                      ...autoUpdatedApps[autoAppIndex],
                      status: APPLICATION_STATUS.POR_DISPERSAR
                    };

                    setAppsWithApproval(autoUpdatedApps);
                  }
                }
              } catch (error) {
                console.error('Error al mover automáticamente a Por Dispersar:', error);
                setErrorMessage('Error al mover automáticamente la solicitud a Por Dispersar');
              } finally {
                // Limpiar mensaje de transición
                setTimeout(() => setAutoTransitionMessage(null), 5000);
              }
            }, 3000);
          }
        }
      }
    } catch (error) {
      console.error('Error al cambiar el estado de la aplicación:', error);
      setErrorMessage('Error al cambiar el estado de la aplicación');

      // Revertir cambios optimistas en caso de error
      const revertApps = [...appsWithApproval];
      const revertAppIndex = revertApps.findIndex(app => app.id === application.id);

      if (revertAppIndex !== -1) {
        revertApps[revertAppIndex] = {
          ...revertApps[revertAppIndex],
          isMoving: false,
          targetStatus: undefined
        };

        setAppsWithApproval(revertApps);
      }
    }
  };

