import React, { useEffect, useState } from 'react';
import { XMarkIcon } from '@heroicons/react/24/outline';

// Map NotificationType to basic types
const mapNotificationTypeToBasic = (type: string): 'info' | 'success' | 'warning' | 'error' => {
  switch (type) {
    case 'info':
    case 'new_application':
    case 'new_message':
      return 'info';
    case 'success':
    case 'approval_required':
    case 'application_status_updated':
      return 'success';
    case 'warning':
      return 'warning';
    case 'error':
    case 'application_comment':
      return 'error';
    default:
      return 'info';
  }
};

// Add a global listener for user interaction to enable audio
if (typeof window !== 'undefined' && !document.documentElement.hasAttribute('data-listeners-added')) {
  document.documentElement.setAttribute('data-listeners-added', 'true');

  const userInteractionEvents = ['click', 'touchstart', 'keydown'];

  const handleUserInteraction = () => {
    document.documentElement.setAttribute('data-user-interacted', 'true');

    // Remove listeners after first interaction
    userInteractionEvents.forEach(event => {
      document.removeEventListener(event, handleUserInteraction);
    });

    // Play a silent sound to unlock audio on iOS
    try {
      const silentSound = new Audio();
      silentSound.volume = 0.01;
      silentSound.play().catch(() => {/* Ignore errors */});
    } catch (e) {
      // Ignore errors
    }
  };

  // Add listeners for all interaction types
  userInteractionEvents.forEach(event => {
    document.addEventListener(event, handleUserInteraction);
  });

  console.log('User interaction listeners added for sound enablement');
}

interface NotificationPopupProps {
  title: string;
  message: string;
  type?: 'info' | 'success' | 'warning' | 'error' | string;
  duration?: number; // Duración en milisegundos
  onClose?: () => void;
  playSound?: boolean;
  soundType?: 'notification' | 'alert' | 'approval'; // Diferentes tipos de sonido
  customSound?: string; // Ruta personalizada para el sonido
  centerScreen?: boolean; // Opción para centrar en la pantalla
}

const NotificationPopup: React.FC<NotificationPopupProps> = ({
  title,
  message,
  type = 'info',
  duration = 5000, // Por defecto, 5 segundos
  onClose,
  playSound = true,
  soundType = 'notification',
  customSound,
  centerScreen = false
}) => {
  const [isVisible, setIsVisible] = useState(true);
  const [exiting, setExiting] = useState(false);

  // Configurar el auto-cierre
  useEffect(() => {
    if (duration > 0) {
      const timer = setTimeout(() => {
        closeNotification();
      }, duration);
      return () => clearTimeout(timer);
    }
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [duration]);

  // Reproducir sonido de notificación
  useEffect(() => {
    if (playSound) {
      try {
        // Create audio element with direct path
        const soundPath = customSound || 
          (soundType === 'notification' ? '/sounds/notification.mp3' : 
           soundType === 'alert' ? '/sounds/alert.mp3' : 
           soundType === 'approval' ? '/sounds/approval.mp3' : 
           '/sounds/notification.mp3');

        // Create a simple audio element
        const audio = new Audio(soundPath);

        // Set lower volume
        audio.volume = 0.3;

        // Use onClick to unlock audio if needed
        const unlockAudio = () => {
          document.removeEventListener('click', unlockAudio);
          // Restore the previous onclick handlers if there were any

          audio.play().catch(err => {
            console.log('Still unable to play audio:', err);
          });
        };

        // Save current onclick to restore it later
        const oldClick = document.documentElement.onclick;

        // Try to play directly
        audio.play().catch(err => {
          console.log('Browser blocked autoplay, will try after user interaction');

          // If it fails, wait for user interaction
          document.documentElement.onclick = unlockAudio;

          // Restore old handler after 10 seconds if not clicked
          setTimeout(() => {
            document.documentElement.onclick = oldClick;
          }, 10000);
        });
      } catch (error) {
        console.error('Failed to play notification sound:', error);
      }
    }
  }, [playSound, soundType, customSound]);

  const closeNotification = () => {
    setExiting(true);
    // Agregar un pequeño retraso para la animación de salida
    setTimeout(() => {
      setIsVisible(false);
      if (onClose) onClose();
    }, 300);
  };

  if (!isVisible) return null;

  // Mapeo de estilos según el tipo
  const typeStyles = {
    info: {
      bg: 'bg-blue-50',
      border: 'border-blue-200',
      text: 'text-blue-800',
      title: 'text-blue-900'
    },
    success: {
      bg: 'bg-green-50',
      border: 'border-green-200',
      text: 'text-green-800',
      title: 'text-green-900'
    },
    warning: {
      bg: 'bg-yellow-50',
      border: 'border-yellow-200',
      text: 'text-yellow-800',
      title: 'text-yellow-900'
    },
    error: {
      bg: 'bg-red-50',
      border: 'border-red-200',
      text: 'text-red-800',
      title: 'text-red-900'
    }
  };

  // Map complex notification types to the basic ones
  const basicType = mapNotificationTypeToBasic(type);

  // Use the mapped type to get styles
  const styles = typeStyles[basicType] || typeStyles.info;

  // Estilos de posición según si debe estar centrado o no
  const positionClasses = centerScreen 
    ? "fixed inset-0 flex items-center justify-center z-50"
    : "fixed top-5 right-5 z-50 max-w-md w-full";

  // Ancho según si debe estar centrado o no
  const widthClasses = centerScreen 
    ? "w-full max-w-xl mx-4" // Más ancho cuando está centrado
    : "w-full";

  return (
    <div className={positionClasses}>
      <div
        className={`${styles.bg} ${styles.border} ${styles.text} rounded-lg shadow-lg p-4 border ${
          exiting ? 'animate-popup-exit' : 'animate-popup-entry'
        } ${widthClasses}`}
      >
        <div className="flex justify-between items-start">
          <div className="flex-1">
            <h3 className={`text-base font-semibold ${styles.title}`}>{title}</h3>
            {/* Mensaje como HTML si es un popup centrado o como texto normal */}
            {centerScreen ? (
              <div className="mt-2" dangerouslySetInnerHTML={{ __html: message }} />
            ) : (
              <div className="mt-1 text-sm">{message}</div>
            )}
          </div>
          <button
            onClick={closeNotification}
            className="flex-shrink-0 ml-2 text-gray-400 hover:text-gray-500 focus:outline-none"
          >
            <XMarkIcon className="h-5 w-5" />
          </button>
        </div>
      </div>
    </div>
  );
};

export default NotificationPopup;
