import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import MainLayout from '../components/layout/MainLayout';
import { useAuth } from '../contexts/AuthContext';
import { Client, getClientById, createClient, updateClient } from '../services/clientService';
import { toast } from 'react-hot-toast';

const ClientForm: React.FC = () => {
  const { clientId } = useParams<{ clientId: string }>();
  const navigate = useNavigate();
  const { user } = useAuth(); // Para asignar company/advisor si aplica
  const [client, setClient] = useState<Partial<Client>>({}); // Usar Partial para el estado inicial
  const [loading, setLoading] = useState<boolean>(false);
  const [isEditMode, setIsEditMode] = useState<boolean>(false);

  useEffect(() => {
    if (clientId) {
      setIsEditMode(true);
      setLoading(true);
      getClientById(clientId)
        .then((data: Client | null) => {
          if (data) {
            const initialClientState: Partial<Client> = {};
            Object.keys(data).forEach(key => {
              const typedKey = key as keyof Client;
              initialClientState[typedKey] = data[typedKey] === null ? undefined : data[typedKey];
            });
            setClient(initialClientState);
          } else {
            toast.error('Cliente no encontrado.');
            navigate('/clients');
          }
        })
        .catch((err: unknown) => {
          console.error('Error fetching client:', err);
          toast.error('Error al cargar los datos del cliente.');
          navigate('/clients');
        })
        .finally(() => setLoading(false));
    }
  }, [clientId, navigate]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value, type } = e.target;
    const parsedValue = type === 'number' ? (value === '' ? null : parseFloat(value)) : value;
    setClient((prev: Partial<Client>) => ({ ...prev, [name]: parsedValue }));
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setLoading(true);

    try {
      let savedClient: Client | null = null;
      const clientData = { ...client };

      // Asignar company/advisor si es un nuevo cliente y el usuario tiene rol
      if (!isEditMode) {
        if (user?.role === 'COMPANY_ADMIN' && user.entityId) {
          clientData.company_id = user.entityId;
        }
        if (user?.role === 'ADVISOR' && user.id) {
          clientData.advisor_id = user.id;
        }
      }

      if (isEditMode && clientId) {
        // Eliminar id y created_at del objeto de actualización
        const { id, created_at, ...updateData } = clientData;
        savedClient = await updateClient(clientId, updateData as Partial<Client>);
        toast.success('Cliente actualizado correctamente');
      } else {
        // Asegurarse de que los campos obligatorios mínimos estén presentes (ej. name)
        if (!clientData.name) {
          toast.error('El nombre del cliente es obligatorio.');
          setLoading(false);
          return;
        }
        savedClient = await createClient(clientData as Omit<Client, 'id' | 'created_at'>);
        toast.success('Cliente creado correctamente');
      }

      if (savedClient) {
        navigate(`/clients/${savedClient.id}`); // Navegar al detalle del cliente guardado
      } else {
           navigate('/clients'); // O volver a la lista si algo falló silenciosamente
      }
    } catch (error: any) {
      console.error('Error saving client:', error);
      // Mostrar error específico de duplicado si aplica
       if (error.message?.includes('Error de duplicado')) {
          toast.error(`Error al guardar: ${error.message}. Verifique Email, RFC o CURP.`);
       } else {
           toast.error('Error al guardar el cliente. Por favor intente nuevamente.');
       }
    } finally {
      setLoading(false);
    }
  };

  if (loading && isEditMode) {
    return <MainLayout><div className="p-6 text-center"><span className="loading loading-spinner loading-lg"></span></div></MainLayout>;
  }

  return (
    <MainLayout>
      <div className="p-6">
        <h1 className="text-2xl font-bold mb-6">
          {isEditMode ? 'Editar Cliente' : 'Crear Nuevo Cliente'}
        </h1>

        <form onSubmit={handleSubmit} className="card bg-base-100 shadow-xl">
          <div className="card-body grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">

            {/* --- Sección 1: Datos de Contacto y Básicos --- */}
            <div className="form-control md:col-span-1">
              <label className="label"><span className="label-text">Nombre Completo *</span></label>
              <input type="text" name="name" value={client.name || ''} onChange={handleChange} className="input input-bordered" required />
            </div>
            <div className="form-control">
              <label className="label"><span className="label-text">Correo Electrónico</span></label>
              <input type="email" name="email" value={client.email || ''} onChange={handleChange} className="input input-bordered" />
            </div>
            <div className="form-control">
              <label className="label"><span className="label-text">Número de Teléfono</span></label>
              <input type="tel" name="phone" value={client.phone || ''} onChange={handleChange} className="input input-bordered" />
            </div>
            <div className="form-control">
              <label className="label"><span className="label-text">Fecha de Nacimiento</span></label>
              <input type="date" name="birth_date" value={client.birth_date || ''} onChange={handleChange} className="input input-bordered" />
            </div>
            <div className="form-control">
              <label className="label"><span className="label-text">Género</span></label>
              <select name="gender" value={client.gender || ''} onChange={handleChange} className="select select-bordered">
                <option value="">Seleccionar...</option>
                <option value="Masculino">Masculino</option>
                <option value="Femenino">Femenino</option>
                <option value="Otro">Otro</option>
                <option value="Prefiero no decir">Prefiero no decir</option>
              </select>
            </div>
             <div className="form-control">
              <label className="label"><span className="label-text">Estado Civil</span></label>
              <select name="marital_status" value={client.marital_status || ''} onChange={handleChange} className="select select-bordered">
                <option value="">Seleccionar...</option>
                <option value="Soltero/a">Soltero/a</option>
                <option value="Casado/a">Casado/a</option>
                <option value="Divorciado/a">Divorciado/a</option>
                <option value="Viudo/a">Viudo/a</option>
                 <option value="Unión Libre">Unión Libre</option>
              </select>
            </div>

            {/* --- Sección 2: Identificación y Dirección --- */}
             <div className="form-control">
              <label className="label"><span className="label-text">RFC</span></label>
              <input type="text" name="rfc" value={client.rfc || ''} onChange={handleChange} className="input input-bordered" />
            </div>
             <div className="form-control">
              <label className="label"><span className="label-text">CURP</span></label>
              <input type="text" name="curp" value={client.curp || ''} onChange={handleChange} className="input input-bordered" />
            </div>
            <div className="form-control md:col-span-1">
              <label className="label"><span className="label-text">Dirección (Calle y Número)</span></label>
              <input type="text" name="address" value={client.address || ''} onChange={handleChange} className="input input-bordered" />
            </div>
            <div className="form-control">
              <label className="label"><span className="label-text">Ciudad</span></label>
              <input type="text" name="city" value={client.city || ''} onChange={handleChange} className="input input-bordered" />
            </div>
            <div className="form-control">
              <label className="label"><span className="label-text">Estado</span></label>
              <input type="text" name="state" value={client.state || ''} onChange={handleChange} className="input input-bordered" />
            </div>
            <div className="form-control">
              <label className="label"><span className="label-text">Código Postal</span></label>
              <input type="text" name="postal_code" value={client.postal_code || ''} onChange={handleChange} className="input input-bordered" />
            </div>

            {/* --- Sección 3: Empleo e Ingresos --- */}
            <div className="form-control">
              <label className="label"><span className="label-text">Tipo de Empleo</span></label>
              <input type="text" name="employment_type" value={client.employment_type || ''} onChange={handleChange} className="input input-bordered" />
            </div>
            <div className="form-control">
              <label className="label"><span className="label-text">Antigüedad Laboral (Años)</span></label>
              <input type="number" name="employment_years" value={client.employment_years === null ? '' : client.employment_years} onChange={handleChange} className="input input-bordered" min="0" />
            </div>
            <div className="form-control">
              <label className="label"><span className="label-text">Ingreso Mensual ($)</span></label>
              <input type="number" name="monthly_income" value={client.monthly_income === null ? '' : client.monthly_income} onChange={handleChange} className="input input-bordered" min="0" step="0.01" />
            </div>
            <div className="form-control">
              <label className="label"><span className="label-text">Ingresos Adicionales ($)</span></label>
              <input type="number" name="additional_income" value={client.additional_income === null ? '' : client.additional_income} onChange={handleChange} className="input input-bordered" min="0" step="0.01" />
            </div>
            <div className="form-control">
              <label className="label"><span className="label-text">Gastos Mensuales ($)</span></label>
              <input type="number" name="monthly_expenses" value={client.monthly_expenses === null ? '' : client.monthly_expenses} onChange={handleChange} className="input input-bordered" min="0" step="0.01" />
            </div>
             <div className="form-control">
              <label className="label"><span className="label-text">Saldos Otros Créditos ($)</span></label>
              <input type="number" name="other_loan_balances" value={client.other_loan_balances === null ? '' : client.other_loan_balances} onChange={handleChange} className="input input-bordered" min="0" step="0.01" />
            </div>

             {/* --- Sección 4: Datos Bancarios --- */}
             <div className="form-control">
              <label className="label"><span className="label-text">Banco</span></label>
              <input type="text" name="bank_name" value={client.bank_name || ''} onChange={handleChange} className="input input-bordered" />
            </div>
            <div className="form-control">
              <label className="label"><span className="label-text">CLABE Interbancaria</span></label>
              <input type="text" name="bank_clabe" value={client.bank_clabe || ''} onChange={handleChange} className="input input-bordered" />
            </div>
            <div className="form-control">
              <label className="label"><span className="label-text">Número de Cuenta</span></label>
              <input type="text" name="bank_account_number" value={client.bank_account_number || ''} onChange={handleChange} className="input input-bordered" />
            </div>
             <div className="form-control">
              <label className="label"><span className="label-text">Tipo de Cuenta</span></label>
              <input type="text" name="bank_account_type" value={client.bank_account_type || ''} onChange={handleChange} className="input input-bordered" />
            </div>
             <div className="form-control">
              <label className="label"><span className="label-text">Origen de la Cuenta (Estado)</span></label>
              <input type="text" name="bank_account_origin" value={client.bank_account_origin || ''} onChange={handleChange} className="input input-bordered" />
            </div>

          </div>

          <div className="card-actions justify-end p-4 mt-4 border-t border-base-300">
            <button type="button" className="btn btn-ghost" onClick={() => navigate('/clients')}>
              Cancelar
            </button>
            <button type="submit" className="btn btn-primary" disabled={loading}>
              {loading ? <span className="loading loading-spinner loading-xs"></span> : (isEditMode ? 'Guardar Cambios' : 'Crear Cliente')}
            </button>
          </div>
        </form>
      </div>
    </MainLayout>
  );
};

export default ClientForm;
