import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import MainLayout from '../components/layout/MainLayout';
import ClientForm from '../components/clients/ClientForm';
import { Client, ClientDocument, createClient } from '../services/clientService';
import Alert from '../components/ui/Alert';

const NewClient: React.FC = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [successMessage, setSuccessMessage] = useState<string | null>(null);
  const [draftClient, setDraftClient] = useState<Partial<Client> | null>(null);

  const handleSubmit = async (data: Partial<Client>, documents: ClientDocument[]) => {
    try {
      setIsSubmitting(true);
      setError(null);
      setSuccessMessage(null);

      console.log('Creating new client with data:', data);

      // Si el usuario es asesor o admin de empresa, asignar los IDs correspondientes
      const clientData: Partial<Client> = { ...data };

      if (user) {
        if (user.role === 'ADVISOR') {
          clientData.advisor_id = user.id;
        } else if (user.role === 'COMPANY_ADMIN') {
          clientData.company_id = user.entityId;
        }
      }

      // Crear el cliente
      const newClient = await createClient(clientData as Omit<Client, 'id' | 'created_at'>, documents, user?.id);
      console.log('Client created successfully:', newClient);

      // Clear local storage
      localStorage.removeItem('clientFormData');
      localStorage.removeItem('clientFormStep');

      // Redireccionar a la lista de clientes
      navigate('/clients');
    } catch (error: any) {
      console.error('Error al crear cliente:', error);
      setError(error.message || 'Error al crear el cliente. Por favor, intente de nuevo.');
    } finally {
      setIsSubmitting(false);
    }
  };

  // Handle partial save functionality
  const handleSavePartial = async (data: Partial<Client>, step: number) => {
    try {
      console.log(`Saving partial data for new client at step ${step}`);

      // Update the local data with this partial section
      const updatedData = { ...draftClient, ...data };
      setDraftClient(updatedData);

      // For first-time saves, we need to actually create the client in the database
      // This is particularly important for step 1 (personal data)
      if (!draftClient?.id) {
        console.log('No existing client ID found, creating new client record in Supabase');

        // Prepare client data with user associations
        const clientData: Partial<Client> = { ...updatedData };

        // Handle the company_id requirement - this field is NOT NULL in the database
        if (user) {
          if (user.role === 'ADVISOR') {
            clientData.advisor_id = user.id;
            // If user is an advisor, we need to ensure a company_id is provided
            // For advisors, we'll use the default company ID if one isn't explicitly set
            if (!clientData.company_id) {
              // Default company ID from the system (Herramental)
              clientData.company_id = "70b2aa97-a5b6-4b5e-91db-be8acbd3701a"; 
              console.log('Using default company_id for advisor');
            }
          } else if (user.role === 'COMPANY_ADMIN') {
            clientData.company_id = user.entityId;
          } else {
            // For other roles, still need to provide a company_id due to db constraint
            clientData.company_id = "70b2aa97-a5b6-4b5e-91db-be8acbd3701a"; // Default company
            console.log('Using default company_id for user with role:', user.role);
          }
        } else {
          // No user information available, use default company_id
          clientData.company_id = "70b2aa97-a5b6-4b5e-91db-be8acbd3701a"; // Default company
          console.log('No user context, using default company_id');
        }

        try {
          // Create minimal client record with available data
          const newClient = await createClient(clientData as Omit<Client, 'id' | 'created_at'>, [], user?.id);
          console.log('New client record created with ID:', newClient.id);

          // Update draftClient with the newly created client including ID
          setDraftClient({...updatedData, id: newClient.id, created_at: newClient.created_at});
        } catch (err: any) {
          console.error('Error creating initial client record:', err);
          throw new Error(`No se pudo crear el registro del cliente: ${err.message}`);
        }
      } 
      else {
        // For subsequent saves, we would normally update the existing record
        // But since we're in NewClient, we just keep the data locally
        // The final save will happen with the full form submission
        console.log(`Client already exists with ID ${draftClient.id}, storing updates locally`);
      }

      // Create a backup in case the user refreshes
      try {
        localStorage.setItem('clientFormData', JSON.stringify(updatedData));
        localStorage.setItem('clientFormStep', String(step));
      } catch (err) {
        console.error('Error saving form state:', err);
      }

      // Show success message
      setSuccessMessage(`Se guardaron los datos de la sección ${step} correctamente`);
      console.log(`Successfully saved data for section ${step}`);

      // Clear message after delay
      setTimeout(() => {
        setSuccessMessage(null);
      }, 3000);

      return updatedData;
    } catch (error: any) {
      console.error(`Error saving partial data for step ${step}:`, error);
      setError(error.message || `Error al guardar la sección ${step}. Por favor, intente de nuevo.`);
      throw error;
    }
  };

  return (
    <MainLayout>
      <div className="p-6">
        <div className="flex items-center mb-6">
          <button 
            onClick={() => navigate('/clients')} 
            className="btn btn-sm btn-outline mr-4"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M9.707 16.707a1 1 0 01-1.414 0l-6-6a1 1 0 010-1.414l6-6a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l4.293 4.293a1 1 0 010 1.414z" clipRule="evenodd" />
            </svg>
            Volver
          </button>
          <h1 className="text-2xl font-bold">Nuevo Cliente</h1>
        </div>

        {/* Display error message */}
        {error && (
          <div className="mb-4">
            <div className="alert alert-error">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              <span>{error}</span>
              <button onClick={() => setError(null)} className="ml-auto">×</button>
            </div>
          </div>
        )}

        {/* Display success message */}
        {successMessage && (
          <div className="mb-4">
            <div className="alert alert-success">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              <span>{successMessage}</span>
              <button onClick={() => setSuccessMessage(null)} className="ml-auto">×</button>
            </div>
          </div>
        )}

        <ClientForm 
          initialData={draftClient || undefined}
          onSubmit={handleSubmit}
          onSavePartial={handleSavePartial}
          isSubmitting={isSubmitting}
        />
      </div>
    </MainLayout>
  );
};

export default NewClient;
