function escapeSQLString(str: string) {
  if (!str) return '';
  return str.replace(/'/g, "''");
}

export const getClientApplications = async (clientId: string) => {
  try {
    const client = await getClientById(clientId).catch(err => {
      throw err;
    });

    if (!client) {
      console.warn('Client not found. Returning empty applications list.');
      return [];
    }

    const query = `
      SELECT * FROM ${TABLES.APPLICATIONS}
      WHERE client_name = '${escapeSQLString(client.name || '')}'
      ORDER BY created_at DESC
    `;

    const executeQuery = async (query: string) => {
      try {
        const response = await fetch('http://localhost:3100/query', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ query: query }),
        });

        const result = await response.json();

        if (result.error) {
          console.error('Error en la consulta SQL:', result.error);
          throw new Error(result.error);
        }

        return result.data;
      } catch (error) {
        console.error('Error ejecutando la consulta:', error);
        throw error;
      }
    };

    const data = await executeQuery(query);
    return data;
  } catch (error) {
    console.error(`Error fetching applications for client ${clientId}:`, error);
    throw error;
  }
};

export const uploadClientDocuments = async (
  clientId: string, 
  userId: string, 
  documents: ClientDocument[]
) => {
  if (!documents || documents.length === 0) {
    console.log('No documents to upload');
    return {
      successful: [],
      failed: [],
      allSuccessful: true
    };
  }

  console.log(`Attempting to upload ${documents.length} documents for client ${clientId}`);

  // Filter out invalid documents
  const validDocuments = documents.filter(doc => {
    if (!doc.file || !doc.category || !doc.name) {
      console.error('Invalid document detected:', doc);
      return false;
    }
    return true;
  });

  if (validDocuments.length === 0) {
    console.warn('No valid documents to upload after filtering');
    return {
      successful: [],
      failed: [],
      allSuccessful: true
    };
  }

  // Process documents sequentially instead of in parallel to avoid race conditions
  const results = [];
  const failedDocuments = [];

  try {
    // Intentamos verificar primero si el bucket existe para evitar múltiples intentos fallidos
    try {
      await documentService.ensureStorageBucketExists();
    } catch (bucketError) {
      console.warn('Bucket availability check failed, documents will not be uploaded:', bucketError);

      // Agregamos todos los documentos a la lista de fallos pero permitimos que el cliente se actualice
      for (const doc of validDocuments) {
        failedDocuments.push({
          name: doc.name,
          category: doc.category,
          error: 'El almacenamiento de documentos no está disponible temporalmente'
        });
      }

      // Retornamos con un mensaje de advertencia en lugar de lanzar un error
      return {
        successful: [],
        failed: failedDocuments,
        allSuccessful: false,
        warningMessage: 'Se actualizó la información del cliente, pero no se pudieron subir los documentos debido a un problema con el almacenamiento. El administrador debe crear el bucket "documents" en el panel de Supabase Storage.'
      };
    }

    // If we get here, the bucket exists, proceed with document upload
    for (const doc of validDocuments) {
      try {
        // Attempt upload with retries (handled inside uploadDocument)
        console.log(`Attempting to upload document ${doc.name} for client ${clientId}`);

        for (let attempt = 1; attempt <= 3; attempt++) {
          try {
            const result = await documentService.uploadDocument({
              file: doc.file,
              client_id: clientId,
              application_id: undefined,
              userId,
              documentName: doc.name,
              category: doc.category,
              description: doc.description || undefined
            });

            results.push(result);
            console.log(`Successfully uploaded document ${doc.name} (${result.id})`);
            break; // Exit retry loop if successful
          } catch (uploadError) {
            console.error(`Error uploading document ${doc.name} (attempt ${attempt}/3):`, uploadError);

            if (attempt === 3) {
              // Last attempt failed
              failedDocuments.push({
                name: doc.name,
                category: doc.category,
                error: uploadError instanceof Error ? uploadError.message : 'Error desconocido'
              });
            } else {
              // Wait before retry with exponential backoff
              await new Promise(r => setTimeout(r, 1000 * Math.pow(2, attempt - 1)));
              console.log(`Retrying upload for document ${doc.name} (attempt ${attempt + 1}/3)`);
            }
          }
        }
      } catch (docError) {
        console.error(`Failed to upload document ${doc.name}:`, docError);
        failedDocuments.push({
          name: doc.name,
          category: doc.category,
          error: docError instanceof Error ? docError.message : 'Error desconocido'
        });
      }
    }

    if (failedDocuments.length > 0) {
      console.warn(`${failedDocuments.length} documents failed to upload:`, failedDocuments);
    }

    // Even if some documents failed, we still return success with information about failures
    let warningMessage = null;
    if (failedDocuments.length > 0) {
      if (failedDocuments.length === validDocuments.length) {
        warningMessage = 'No se pudieron subir los documentos, pero se guardó la información del cliente.';
      } else {
        warningMessage = `Se subieron ${results.length} documentos, pero ${failedDocuments.length} fallaron.`;
      }
    }

    return {
      successful: results,
      failed: failedDocuments,
      allSuccessful: failedDocuments.length === 0,
      warningMessage
    };
  } catch (error) {
    console.error('Critical error in uploadClientDocuments:', error);

    // Provide more detailed error message
    let errorMessage = 'Hubo un problema al subir los documentos. Por favor, intente de nuevo más tarde.';

    if (error instanceof Error) {
      if (error.message.includes('no existe') || error.message.includes('Bucket not found')) {
        errorMessage = 'Error de configuración: El almacenamiento de documentos no está disponible. Por favor, contacte al administrador.';
      } else if (error.message.includes('Permission denied')) {
        errorMessage = 'No tiene permiso para subir documentos. Por favor, contacte al administrador.';
      } else {
        errorMessage = `Error: ${error.message}`;
      }
    }

    throw new Error(errorMessage);
  }
};

