export const updateClient = async (id: string, updates: Partial<Client>, documents?: ClientDocument[], userId?: string) => {
  try {
    console.log(`Starting client update for ID ${id}`);

    // Create a copy to avoid modifying the original object
    const userUpdates = {
      email: updates.email,
      first_name: updates.first_name,
      paternal_surname: updates.paternal_surname,
      maternal_surname: updates.maternal_surname,
      phone: updates.phone,
      birth_date: updates.birth_date,
      company_id: updates.company_id,
      rfc: updates.rfc,
      curp: updates.curp,
      advisor_id: updates.advisor_id,
      address: updates.address,
      city: updates.city,
      state: updates.state,
      postal_code: updates.postal_code,
      gender: updates.gender,
      marital_status: updates.marital_status,
      employment_type: updates.employment_type,
      employment_years: updates.employment_years,
      monthly_income: updates.monthly_income,
      additional_income: updates.additional_income,
      monthly_expenses: updates.monthly_expenses,
      other_loan_balances: updates.other_loan_balances,
      bank_name: updates.bank_name,
      bank_clabe: updates.bank_clabe,
      bank_account_number: updates.bank_account_number,
      bank_account_type: updates.bank_account_type,
      bank_account_origin: updates.bank_account_origin,
    };

    // Validate and sanitize data
    // Convert empty strings to null for numeric fields
    ['employment_years', 'monthly_income', 'additional_income', 
     'monthly_expenses', 'other_loan_balances'].forEach(field => {
      const key = field as keyof typeof userUpdates;
      if (userUpdates[key] === '') {
        userUpdates[key] = undefined;
      }
    });

    // Remove undefined fields and empty strings from non-required fields
    Object.keys(userUpdates).forEach(key => {
      const typedKey = key as keyof typeof userUpdates;
      if (userUpdates[typedKey] === undefined) {
        delete userUpdates[typedKey];
      }
    });

    console.log(`Updating client ${id} with sanitized data:`, JSON.stringify(userUpdates));

    // First, verify the client exists
    const { data: existingClient, error: existingError } = await supabase
      .from(USERS_TABLE)
      .select('id')
      .eq('id', id)
      .single();

    if (existingError || !existingClient) {
      console.error(`Client with ID ${id} not found:`, existingError);
      throw new Error(`No se encontró el cliente con ID ${id}. Verifique que el cliente exista.`);
    }

    // Perform the update
    console.log(`Client exists, proceeding with update for ID ${id}`);
    const { data, error } = await supabase
      .from(USERS_TABLE)
      .update(userUpdates)
      .eq('id', id)
      .select();

    if (error) {
      console.error(`Error updating client with ID ${id}:`, error);
      throw error;
    }

    let updatedClient: Client;

    if (!data || data.length === 0) {
      console.log(`No data returned when updating client with ID ${id}, fetching client data separately`);

      // Fallback: fetch the client data separately
      const { data: fetchedData, error: fetchError } = await supabase
        .from(USERS_TABLE)
        .select('*')
        .eq('id', id)
        .single();

      if (fetchError) {
        console.error(`Error fetching updated client with ID ${id}:`, fetchError);
        throw new Error('El cliente fue actualizado pero no se pudieron recuperar sus datos. Por favor, recargue la página.');
      }

      if (!fetchedData) {
        console.error(`No data found for client with ID ${id} after update`);
        throw new Error('No se encontró el cliente después de la actualización. Por favor, verifique que el cliente exista.');
      }

      console.log(`Successfully retrieved client data after update for ID ${id}`);
      updatedClient = mapUserToClient(fetchedData);
    } else {
      console.log(`Successfully updated client with data returned, ID ${id}`);
      updatedClient = mapUserToClient(data[0]);
    }

    let documentResult = null;

    // Upload documents if provided
    if (documents && documents.length > 0 && userId) {
      try {
        console.log(`Uploading ${documents.length} documents for client ${id}`);
        documentResult = await uploadClientDocuments(id, userId, documents);

        // Check if any documents failed to upload
        if (documentResult && !documentResult.allSuccessful) {
          console.warn(`${documentResult.failed.length} documents failed to upload during client update`);

          // We'll continue but note the warning
          updatedClient.warningMessage = `Se actualizó el cliente, pero ${documentResult.failed.length} ${documentResult.failed.length === 1 ? 'documento falló' : 'documentos fallaron'} al subirse. Puede intentar agregarlos nuevamente más tarde.`;
        }
      } catch (docError) {
        console.error(`Error uploading documents for client ${id}:`, docError);

        // Continue with client update but add warning
        updatedClient.warningMessage = 'Se actualizó el cliente, pero hubo un problema al subir los documentos. Puede intentar agregarlos nuevamente más tarde.';
      }
    }

    console.log(`Client update complete for ID ${id}`);
    return updatedClient;
  } catch (error) {
    console.error(`Error in updateClient:`, error);
    throw error;
  }
};

