import React, { useEffect, useState } from 'react';
import { supabase } from '../lib/supabaseClient';
import { PERMISSIONS } from '../utils/constants/permissions';
import { useAuth } from '../contexts/AuthContext';
import { usePermissions } from '../contexts/PermissionsContext';
import MainLayout from '../components/layout/MainLayout';

type DashboardStat = {
  label: string;
  value: number;
  description: string;
  color: string;
};

type RecentApplication = {
  id: string;
  created_at: string;
  applicant_name: string;
  status: string;
  amount: number;
};

const Dashboard: React.FC = () => {
  const { userCan } = usePermissions();
  const { user } = useAuth();
  const [stats, setStats] = useState<DashboardStat[]>([]);
  const [recentApplications, setRecentApplications] = useState<RecentApplication[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchDashboardData = async () => {
      setIsLoading(true);

      try {
        // In a real app, these would be actual queries to your database
        // For now, we'll use mock data

        // Stats data
        setStats([
          {
            label: 'Solicitudes Nuevas',
            value: 12,
            description: 'Últimos 7 días',
            color: 'bg-blue-500',
          },
          {
            label: 'En Proceso',
            value: 8,
            description: 'Solicitudes en revisión',
            color: 'bg-yellow-500',
          },
          {
            label: 'Aprobadas',
            value: 5,
            description: 'Últimos 30 días',
            color: 'bg-green-500',
          },
          {
            label: 'Rechazadas',
            value: 3,
            description: 'Últimos 30 días',
            color: 'bg-red-500',
          },
        ]);

        // Recent applications
        setRecentApplications([
          {
            id: '1',
            created_at: '2023-07-18T10:30:00',
            applicant_name: 'Juan Pérez',
            status: 'pending',
            amount: 25000,
          },
          {
            id: '2',
            created_at: '2023-07-17T14:20:00',
            applicant_name: 'María González',
            status: 'review',
            amount: 50000,
          },
          {
            id: '3',
            created_at: '2023-07-16T09:15:00',
            applicant_name: 'Carlos Rodríguez',
            status: 'approved',
            amount: 35000,
          },
          {
            id: '4',
            created_at: '2023-07-15T16:45:00',
            applicant_name: 'Ana Martínez',
            status: 'rejected',
            amount: 75000,
          },
        ]);
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  const getStatusClass = (status: string) => {
    switch (status) {
      case 'pending':
        return 'badge-warning';
      case 'review':
        return 'badge-info';
      case 'approved':
        return 'badge-success';
      case 'rejected':
        return 'badge-error';
      default:
        return 'badge-ghost';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'pending':
        return 'Pendiente';
      case 'review':
        return 'En Revisión';
      case 'approved':
        return 'Aprobada';
      case 'rejected':
        return 'Rechazada';
      default:
        return status;
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('es-ES', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-CL', {
      style: 'currency',
      currency: 'CLP',
    }).format(amount);
  };

  if (!userCan(PERMISSIONS.VIEW_DASHBOARD)) {
    return (
      <MainLayout>
        <div className="flex items-center justify-center h-96">
          <div className="text-center">
            <h2 className="text-2xl font-bold text-gray-700">Acceso Restringido</h2>
            <p className="text-gray-500 mt-2">No tienes permisos para ver esta página.</p>
          </div>
        </div>
      </MainLayout>
    );
  }

  return (
    <MainLayout>
      <div className="p-6">
        <h1 className="text-2xl font-bold mb-6">Dashboard</h1>

        {isLoading ? (
          <div className="flex justify-center items-center h-64">
            <span className="loading loading-spinner loading-lg"></span>
          </div>
        ) : (
          <>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
              {stats.map((stat, index) => (
                <div key={index} className="stats shadow">
                  <div className="stat">
                    <div className="stat-figure text-primary">
                      <div className={`w-4 h-4 rounded-full ${stat.color}`}></div>
                    </div>
                    <div className="stat-title">{stat.label}</div>
                    <div className="stat-value">{stat.value}</div>
                    <div className="stat-desc">{stat.description}</div>
                  </div>
                </div>
              ))}
            </div>

            <div className="card bg-base-100 shadow-xl">
              <div className="card-body">
                <h2 className="card-title mb-4">Solicitudes Recientes</h2>

                <div className="overflow-x-auto">
                  <table className="table table-zebra w-full">
                    <thead>
                      <tr>
                        <th>Fecha</th>
                        <th>Solicitante</th>
                        <th>Estado</th>
                        <th>Monto</th>
                        <th>Acciones</th>
                      </tr>
                    </thead>
                    <tbody>
                      {recentApplications.map((app) => (
                        <tr key={app.id}>
                          <td>{formatDate(app.created_at)}</td>
                          <td>{app.applicant_name}</td>
                          <td>
                            <div className={`badge ${getStatusClass(app.status)}`}>
                              {getStatusText(app.status)}
                            </div>
                          </td>
                          <td>{formatCurrency(app.amount)}</td>
                          <td>
                            <button className="btn btn-xs btn-outline btn-primary">Ver</button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </>
        )}
      </div>
    </MainLayout>
  );
};

export default Dashboard;
