import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { USER_ROLES } from '../utils/constants/roles';

// Interfaces para tipado
interface User {
  id: string;
  email: string;
  name: string;
  role: string;
  entityId: string;
}

interface Session {
  access_token: string;
  refresh_token: string;
  token_type: string;
  user: User;
  expires_in: number;
}

interface AuthContextType {
  session: Session | null;
  user: User | null;
  loading: boolean;
  error: string | null;
  signIn: (email: string, password: string) => Promise<{ data: any, error: any }>;
  signInWithCode: (accessCode: string, userType: string) => Promise<{ data: any, error: any }>;
  signOut: () => void;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [session, setSession] = useState<Session | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Simular verificación de sesión al iniciar
  useEffect(() => {
    const checkStoredUser = () => {
      try {
        const storedUser = localStorage.getItem('crm_user');
        if (storedUser) {
          setUser(JSON.parse(storedUser));
        }
      } catch (error) {
        console.error('Error checking stored user:', error);
      } finally {
        setLoading(false);
      }
    };

    checkStoredUser();
  }, []);

  /**
   * Inicia sesión con email y password (simulado para compatibilidad)
   */
  const signIn = async (email: string, password: string) => {
    try {
      setLoading(true);

      // En esta versión del MCP no implementamos login con email/password
      // Solo mantenemos la función para compatibilidad con el componente Login
      console.log('Intento de login con email/password:', email);

      // Simulamos un error por ahora
      setError('El inicio de sesión con email/password no está implementado. Por favor use el código de acceso.');
      return { data: null, error: 'Método no implementado' };
    } catch (error) {
      console.error('Error en inicio de sesión:', error);
      setError(error instanceof Error ? error.message : 'Error en el inicio de sesión');
      return { data: null, error };
    } finally {
      setLoading(false);
    }
  };

  /**
   * Inicia sesión usando un código de acceso
   * @param accessCode Código de acceso del usuario
   * @param userType Tipo de usuario (ADVISOR o COMPANY_ADMIN)
   */
  const signInWithCode = async (accessCode: string, userType: string) => {
    try {
      setLoading(true);

      // Consultar la base de datos por el access_code
      let queryUrl = 'http://localhost:3000/query';
      let queryBody = {};

      if (userType === USER_ROLES.ADVISOR) {
        queryBody = {
          query: `SELECT * FROM advisors WHERE access_code = '${accessCode}' LIMIT 1`
        };
      } else if (userType === USER_ROLES.COMPANY_ADMIN) {
        queryBody = {
          query: `SELECT * FROM company_admins WHERE access_code = '${accessCode}' LIMIT 1`
        };
      } else {
        return { data: null, error: "Tipo de usuario no válido" };
      }

      console.log('Consultando usuario con código:', accessCode, 'tipo:', userType);

      const response = await fetch(queryUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(queryBody)
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Error en la consulta: ${errorText}`);
      }

      const result = await response.json();
      console.log('Resultado autenticación:', result);

      if (!result.data || result.data.length === 0) {
        setError('Código de acceso no válido');
        setLoading(false);
        return { data: null, error: "Código de acceso no válido" };
      }

      const userData = result.data[0];
      console.log('Usuario encontrado:', userData);

      // Crear objeto de usuario
      const userInfo: User = {
        id: userData.id,
        email: userData.email || '',
        name: userData.name || '',
        role: userType,
        entityId: userType === USER_ROLES.COMPANY_ADMIN ? userData.company_id : userData.id,
      };

      // Crear objeto de sesión
      const sessionData: Session = {
        access_token: 'simulated-access-token',
        refresh_token: 'simulated-refresh-token',
        token_type: 'bearer',
        user: userInfo,
        expires_in: 3600
      };

      // Establecer la sesión
      setSession(sessionData);
      setUser(userInfo);
      setError(null);

      // Guardar la sesión en localStorage
      localStorage.setItem('authSession', JSON.stringify(sessionData));
      localStorage.setItem('crm_user', JSON.stringify(userInfo));

      console.log('Sesión establecida:', sessionData);

      return { data: sessionData, error: null };
    } catch (error) {
      console.error('Error al iniciar sesión con código:', error);
      setError(error instanceof Error ? error.message : 'Error en el inicio de sesión');
      return { data: null, error };
    } finally {
      setLoading(false);
    }
  };

  const signOut = async () => {
    try {
      // Eliminar datos de sesión
      localStorage.removeItem('crm_user');
      localStorage.removeItem('authSession');
      setUser(null);
      setSession(null);
    } catch (error) {
      console.error('Error signing out:', error);
    }
  };

  const value = {
    user,
    session,
    loading,
    error,
    signIn,
    signInWithCode,
    signOut,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
