import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Application, getApprovalStatus } from '../../services/applicationService';
import { STATUS_COLORS, APPLICATION_STATUS, STATUS_LABELS } from '../../utils/constants/statuses';
import { usePermissions } from '../../contexts/PermissionsContext';

// Función para escapar cadenas de texto para SQL
function escapeSQLString(str: string) {
  if (!str) return '';
  return str.replace(/'/g, "''");
}

interface KanbanBoardProps {
  applications: Application[];
  onStatusChange?: (application: Application, newStatus: string) => Promise<void>;
}

// Interfaz para el estado de aprobación
interface ApprovalStatus {
  approvedByAdvisor: boolean;
  approvedByCompany: boolean;
}

// Crear un tipo para aplicaciones con su estado de aprobación
type ApplicationWithApproval = Application & {
  approvalStatus?: ApprovalStatus;
};

const KanbanBoard: React.FC<KanbanBoardProps> = ({ applications, onStatusChange }) => {
  const [draggingId, setDraggingId] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState<string | null>(null);
  const [appsWithApproval, setAppsWithApproval] = useState<ApplicationWithApproval[]>([]);
  const { shouldFilterByEntity, getEntityFilter } = usePermissions();

  // Cargar los estados de aprobación para cada aplicación
  useEffect(() => {
    const loadApprovalStatuses = async () => {
      const entityFilter = shouldFilterByEntity() ? getEntityFilter() : null;

      const updatedApps = await Promise.all(
        applications.map(async (app) => {
          try {
            const approvalStatus = await getApprovalStatus(app.id, entityFilter);
            return { ...app, approvalStatus };
          } catch (error) {
            console.error(`Error loading approval status for app ${app.id}:`, error);
            return app;
          }
        })
      );

      setAppsWithApproval(updatedApps);
    };

    loadApprovalStatuses();
  }, [applications, shouldFilterByEntity, getEntityFilter]);

  // Agrupar aplicaciones por estatus
  const groupedApplications = appsWithApproval.reduce((acc, app) => {
    if (!acc[app.status]) {
      acc[app.status] = [];
    }
    acc[app.status].push(app);
    return acc;
  }, {} as Record<string, ApplicationWithApproval[]>);

  // Asegurar que todas las columnas de estatus estén presentes
  const statuses = Object.values(APPLICATION_STATUS);
  const columns = statuses.map(status => ({
    id: status,
    title: STATUS_LABELS[status as keyof typeof STATUS_LABELS],
    color: STATUS_COLORS[status as keyof typeof STATUS_COLORS],
    applications: groupedApplications[status] || []
  }));

  const handleDragStart = (applicationId: string) => {
    setDraggingId(applicationId);
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
  };

  const handleDrop = async (e: React.DragEvent, newStatus: string) => {
    e.preventDefault();

    if (!draggingId || !onStatusChange) return;

    const application = appsWithApproval.find(app => app.id === draggingId);
    if (!application) return;

    // No hacer nada si el estatus es el mismo
    if (application.status === newStatus) {
      setDraggingId(null);
      return;
    }

    try {
      setIsLoading(true);
      setErrorMessage(null);
      await onStatusChange(application, newStatus);
    } catch (error: any) {
      setErrorMessage(`Error al actualizar el estatus: ${error.message}`);
      console.error("Error updating status:", error);
    } finally {
      setIsLoading(false);
      setDraggingId(null);
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-CL', {
      style: 'currency',
      currency: 'CLP',
    }).format(amount);
  };

  // Renderizar los indicadores de aprobación
  const renderApprovalIndicators = (app: ApplicationWithApproval) => {
    if (!app.approvalStatus) return null;

    return (
      <div className="flex items-center mt-2 space-x-1">
        <div className="tooltip tooltip-top" data-tip={app.approvalStatus.approvedByAdvisor ? "Aprobado por asesor" : "Pendiente de aprobación por asesor"}>
          <div className={`w-3 h-3 rounded-full ${app.approvalStatus.approvedByAdvisor ? 'bg-success' : 'bg-warning'}`}></div>
        </div>
        <div className="tooltip tooltip-top" data-tip={app.approvalStatus.approvedByCompany ? "Aprobado por empresa" : "Pendiente de aprobación por empresa"}>
          <div className={`w-3 h-3 rounded-full ${app.approvalStatus.approvedByCompany ? 'bg-success' : 'bg-warning'}`}></div>
        </div>
      </div>
    );
  };

  return (
    <div className="w-full overflow-x-auto">
      {isLoading && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl flex flex-col items-center">
            <span className="loading loading-spinner loading-lg"></span>
            <p className="mt-4 text-lg font-medium">Actualizando estado...</p>
          </div>
        </div>
      )}

      {errorMessage && (
        <div className="mb-4 alert alert-error shadow-lg">
          <div>
            <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current flex-shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <span>{errorMessage}</span>
          </div>
          <div className="flex-none">
            <button className="btn btn-sm" onClick={() => setErrorMessage(null)}>Cerrar</button>
          </div>
        </div>
      )}

      <div className="flex space-x-4 pb-4 kanban-container" style={{ minWidth: '1000px' }}>
        {columns.map(column => (
          <div 
            key={column.id}
            className={`flex-1 bg-base-200 rounded-box p-4 kanban-column`}
            onDragOver={handleDragOver}
            onDrop={(e) => handleDrop(e, column.id)}
          >
            <div className={`text-center mb-4 font-bold bg-${column.color} text-${column.color}-content p-2 rounded-md`}>
              {column.title} <span className="badge badge-sm">{column.applications.length}</span>
            </div>

            <div className="space-y-3 min-h-[400px]">
              {column.applications.map(app => (
                <div
                  key={app.id}
                  className="card bg-base-100 shadow-sm hover:shadow-md cursor-move transition-shadow"
                  draggable={!!onStatusChange}
                  onDragStart={() => handleDragStart(app.id)}
                >
                  <div className="card-body p-4">
                    <div className="flex justify-between items-center">
                      <h3 className="text-sm font-semibold truncate">{app.client_name}</h3>
                      <span className="badge badge-primary badge-sm">{app.product_type}</span>
                    </div>

                    <div className="mt-2">
                      <div className="text-xs text-gray-500">
                        {app.client_email}
                      </div>
                      <div className="mt-2 text-sm font-medium">
                        {formatCurrency(app.requested_amount)}
                      </div>

                      {/* Indicadores de estado de aprobación */}
                      <div className="mt-2 flex justify-between items-center">
                        {renderApprovalIndicators(app)}

                        <div className="card-actions justify-end">
                          <Link to={`/applications/${app.id}`} className="btn btn-xs btn-outline">
                            Ver
                          </Link>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default KanbanBoard;
