import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Client } from '../../services/clientService';
import DocumentUploader from '../documents/DocumentUploader';
import { useAuth } from '../../contexts/AuthContext';
import { executeQuery } from '../../services/mcpService';

// Interfaz para documentos
interface ClientDocument {
  id?: string;
  file: File;
  category: string;
  name: string;
}

// Props para el componente
interface ClientFormProps {
  initialData?: Partial<Client>;
  onSubmit: (data: Partial<Client>, documents: ClientDocument[]) => Promise<void>;
  onSavePartial?: (data: Partial<Client>, step: number) => Promise<any>;
  isSubmitting: boolean;
}

// Categor칤as de documentos predefinidas
const DOCUMENT_CATEGORIES = [
  { value: 'identificacion', label: 'Identificaci칩n Oficial' },
  { value: 'comprobante_domicilio', label: 'Comprobante de Domicilio' },
  { value: 'comprobante_ingresos', label: 'Comprobante de Ingresos' },
  { value: 'estados_cuenta', label: 'Estados de Cuenta Bancarios' },
  { value: 'rfc', label: 'Constancia de Situaci칩n Fiscal (RFC)' },
  { value: 'curp', label: 'CURP' },
  { value: 'otros', label: 'Otros Documentos' },
];

// Tipos de empleo
const EMPLOYMENT_TYPES = [
  { value: 'empleado', label: 'Empleado' },
  { value: 'autonomo', label: 'Aut칩nomo / Freelance' },
  { value: 'empresario', label: 'Empresario' },
  { value: 'jubilado', label: 'Jubilado' },
  { value: 'desempleado', label: 'Desempleado' },
  { value: 'estudiante', label: 'Estudiante' },
  { value: 'otro', label: 'Otro' },
];

// Estados civiles
const MARITAL_STATUS_TYPES = [
  { value: 'soltero', label: 'Soltero/a' },
  { value: 'casado', label: 'Casado/a' },
  { value: 'divorciado', label: 'Divorciado/a' },
  { value: 'viudo', label: 'Viudo/a' },
  { value: 'union_libre', label: 'Uni칩n Libre' },
];

// Tipos de cuenta bancaria
const BANK_ACCOUNT_TYPES = [
  { value: 'nomina', label: 'N칩mina' },
  { value: 'ahorro', label: 'Ahorro' },
  { value: 'cheques', label: 'Cheques' },
  { value: 'inversiones', label: 'Inversiones' },
];

const ClientForm: React.FC<ClientFormProps> = ({ initialData = {}, onSubmit, onSavePartial, isSubmitting }) => {
  const navigate = useNavigate();
  const { user } = useAuth();

  // Estado para los datos del formulario
  const [formData, setFormData] = useState<Partial<Client>>({
    first_name: '',
    paternal_surname: '',
    maternal_surname: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    state: '',
    postal_code: '',
    birth_date: '',
    gender: '',
    marital_status: '',
    employment_type: '',
    employment_years: undefined,
    rfc: '',
    curp: '',
    monthly_income: undefined,
    additional_income: undefined,
    monthly_expenses: undefined,
    other_loan_balances: undefined,
    bank_name: '',
    bank_clabe: '',
    bank_account_number: '',
    bank_account_type: '',
    bank_account_origin: '',
    company_id: '',
    ...initialData,
  });

  // Estado para errores de validaci칩n
  const [errors, setErrors] = useState<Record<string, string>>({});

  // Estado para documentos
  const [documents, setDocuments] = useState<ClientDocument[]>([]);

  // Estado para el paso actual del formulario
  const [currentStep, setCurrentStep] = useState(1);

  // Estado para empresas
  const [companies, setCompanies] = useState<{id: string, name: string}[]>([]);
  const [loadingCompanies, setLoadingCompanies] = useState(true);

  // Calcular el progreso de llenado de formulario
  const calculateProgress = () => {
    let filledFields = 0;
    let totalFields = 0;

    // Paso 1: Datos Personales B치sicos
    const step1Fields = ['first_name', 'paternal_surname', 'maternal_surname', 'email', 'phone', 'rfc', 'curp', 'birth_date', 'gender', 'marital_status', 'address', 'city', 'state', 'postal_code', 'company_id'];
    totalFields += step1Fields.length;
    filledFields += step1Fields.filter(field => formData[field as keyof Partial<Client>]).length;

    // Paso 2: Detalles Financieros
    const step2Fields = ['employment_type', 'employment_years', 'monthly_income', 'additional_income', 'monthly_expenses', 'other_loan_balances'];
    totalFields += step2Fields.length;
    filledFields += step2Fields.filter(field => formData[field as keyof Partial<Client>] !== undefined && formData[field as keyof Partial<Client>] !== '').length;

    // Paso 3: Datos Bancarios
    const step3Fields = ['bank_name', 'bank_clabe', 'bank_account_number', 'bank_account_type', 'bank_account_origin'];
    totalFields += step3Fields.length;
    filledFields += step3Fields.filter(field => formData[field as keyof Partial<Client>]).length;

    // Calcular porcentaje
    return Math.round((filledFields / totalFields) * 100);
  };

  // State for progress
  const [progress, setProgress] = useState(() => calculateProgress());

  // Update progress when form data changes
  useEffect(() => {
    setProgress(calculateProgress());
  }, [formData]);

  // Fetch companies on component mount
  useEffect(() => {
    const fetchCompanies = async () => {
      try {
        console.log('Obteniendo lista de empresas...');
        setLoadingCompanies(true);

        const response = await executeQuery('SELECT id, name FROM companies ORDER BY name ASC');
        console.log('Respuesta de empresas:', response);

        if (response && response.data) {
          setCompanies(response.data);
          console.log('Empresas cargadas:', response.data.length);
        } else {
          console.error('No se pudieron cargar las empresas');
        }
      } catch (err) {
        console.error('Error al cargar empresas:', err);
      } finally {
        setLoadingCompanies(false);
      }
    };

    fetchCompanies();
  }, []);

  // Manejar cambios en el formulario
  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value, type } = e.target as HTMLInputElement;

    // Convertir a n칰mero si es un campo num칠rico
    if (type === 'number') {
      setFormData(prev => ({
        ...prev,
        [name]: value === '' ? undefined : parseFloat(value)
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }

    // Limpiar error si existe
    if (errors[name]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[name];
        return newErrors;
      });
    }
  };

  // Manejar documentos agregados
  const handleDocumentAdded = (document: ClientDocument) => {
    setDocuments(prev => [...prev, document]);
  };

  // Manejar documentos eliminados
  const handleDocumentRemoved = (index: number) => {
    setDocuments(prev => prev.filter((_, i) => i !== index));
  };

  // Handle company selection
  const handleCompanyChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const { value } = e.target;
    console.log('Empresa seleccionada:', value);

    setFormData(prev => ({
      ...prev,
      company_id: value
    }));

    if (errors.company_id) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors.company_id;
        return newErrors;
      });
    }
  };

  // Validar datos antes de enviar
  const validateForm = () => {
    const newErrors: Record<string, string> = {};

    // Validar campos obligatorios solo para el paso 1 (datos personales b치sicos)
    if (!formData.first_name?.trim()) {
      newErrors.first_name = 'El nombre es obligatorio';
    }

    if (!formData.paternal_surname?.trim()) {
      newErrors.paternal_surname = 'El apellido paterno es obligatorio';
    }

    // Validate company_id is selected
    if (!formData.company_id) {
      newErrors.company_id = 'Debe seleccionar una empresa';
    }

    // Validar formato de email solo si se ha ingresado algo
    if (formData.email && !/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'El correo electr칩nico no es v치lido';
    }

    // Validar RFC solo si se ha ingresado algo
    if (formData.rfc && !/^([A-Z칌&]{3,4})(\d{6})([A-Z\d]{3})$/.test(formData.rfc.toUpperCase())) {
      newErrors.rfc = 'El RFC no tiene un formato v치lido';
    }

    // Validar CURP solo si se ha ingresado algo
    if (formData.curp && !/^([A-Z][AEIOUX][A-Z]{2}\d{2}(?:0[1-9]|1[0-2])(?:0[1-9]|[12]\d|3[01])[HM](?:AS|B[CS]|C[CLMSH]|D[FG]|G[TR]|HG|JC|M[CNS]|N[ETL]|OC|PL|Q[TR]|S[PLR]|T[CSL]|VZ|YN|ZS)[B-DF-HJ-NP-TV-Z]{3}[A-Z\d])(\d)$/.test(formData.curp.toUpperCase())) {
      newErrors.curp = 'La CURP no tiene un formato v치lido';
    }

    // Validar CLABE solo si se ha ingresado algo
    if (formData.bank_clabe && !/^\d{18}$/.test(formData.bank_clabe)) {
      newErrors.bank_clabe = 'La CLABE debe tener 18 d칤gitos';
    }

    // Establecer errores
    setErrors(newErrors);

    // Solo requerimos que los campos de nombre y apellido paterno est칠n completos para el env칤o
    return !newErrors.first_name && !newErrors.paternal_surname;
  };

  // Validar solo los campos relevantes para el paso actual
  const validateCurrentStep = () => {
    console.log('游댌 Validando paso actual:', currentStep);
    console.log('游댌 Datos actuales:', formData);

    const newErrors: Record<string, string> = {};

    if (currentStep === 1) {
      // Validar que se haya seleccionado una empresa
      if (!formData.company_id) {
        newErrors.company_id = 'Debe seleccionar una empresa';
        console.log('游댌 Error: No se ha seleccionado empresa');
      }

      // Validar campos obligatorios
      if (!formData.first_name?.trim()) {
        newErrors.first_name = 'El nombre es obligatorio';
        console.log('游댌 Error: Nombre obligatorio no proporcionado');
      }

      if (!formData.paternal_surname?.trim()) {
        newErrors.paternal_surname = 'El apellido paterno es obligatorio';
        console.log('游댌 Error: Apellido paterno obligatorio no proporcionado');
      }

      // Validar email, RFC y CURP solo si se han ingresado
      if (formData.email && !/\S+@\S+\.\S+/.test(formData.email)) {
        newErrors.email = 'El correo electr칩nico no es v치lido';
      }

      if (formData.rfc && !/^([A-Z칌&]{3,4})(\d{6})([A-Z\d]{3})$/.test(formData.rfc.toUpperCase())) {
        newErrors.rfc = 'El RFC no tiene un formato v치lido';
      }

      if (formData.curp && !/^([A-Z][AEIOUX][A-Z]{2}\d{2}(?:0[1-9]|1[0-2])(?:0[1-9]|[12]\d|3[01])[HM](?:AS|B[CS]|C[CLMSH]|D[FG]|G[TR]|HG|JC|M[CNS]|N[ETL]|OC|PL|Q[TR]|S[PLR]|T[CSL]|VZ|YN|ZS)[B-DF-HJ-NP-TV-Z]{3}[A-Z\d])(\d)$/.test(formData.curp.toUpperCase())) {
        newErrors.curp = 'La CURP no tiene un formato v치lido';
      }
    } else if (currentStep === 3) {
      // Validar CLABE solo si se ha ingresado
      if (formData.bank_clabe && !/^\d{18}$/.test(formData.bank_clabe)) {
        newErrors.bank_clabe = 'La CLABE debe tener 18 d칤gitos';
      }
    }

    console.log('游댌 Errores de validaci칩n:', newErrors);
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Manejar env칤o del formulario
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Only validate first name and paternal surname
    const nameErrors: Record<string, string> = {};
    if (!formData.first_name?.trim()) {
      nameErrors['first_name'] = 'El nombre es obligatorio';
    }

    if (!formData.paternal_surname?.trim()) {
      nameErrors['paternal_surname'] = 'El apellido paterno es obligatorio';
    }

    if (Object.keys(nameErrors).length > 0) {
      setErrors(nameErrors);
      alert('Por favor, completa al menos el nombre y apellido paterno para guardar');
      setCurrentStep(1); // Go back to personal data section
      return;
    }

    try {
      // Show loader during submission
      await onSubmit(formData, documents);

      // Clean up localStorage on successful submission
      localStorage.removeItem('clientFormData');
      localStorage.removeItem('clientFormStep');
    } catch (error: any) {
      console.error('Error al guardar cliente:', error);
      alert(`Error al guardar: ${error.message || 'Error desconocido'}`);
    }
  };

  // Avanzar al siguiente paso
  const nextStep = () => {
    // Always allow navigation forward regardless of validation
    if (currentStep < 4) {
      // Save the current state to localStorage for recovery in case of crash
      try {
        localStorage.setItem('clientFormData', JSON.stringify(formData));
        localStorage.setItem('clientFormStep', String(currentStep + 1));
      } catch (err) {
        console.error('Error saving form state:', err);
      }

      setCurrentStep(prev => prev + 1);
    }
  };

  // Try to recover form data on component mount
  useEffect(() => {
    console.log('游댌 Intentando recuperar datos del formulario del localStorage...');
    console.log('游댌 initialData:', initialData);

    try {
      const savedData = localStorage.getItem('clientFormData');
      const savedStep = localStorage.getItem('clientFormStep');

      console.log('游댌 savedData del localStorage:', savedData);
      console.log('游댌 savedStep del localStorage:', savedStep);

      if (savedData && Object.keys(initialData).length === 0) {
        // Only restore if we're creating a new client (not editing)
        console.log('游댌 Recuperando datos guardados para un nuevo cliente');
        const parsedData = JSON.parse(savedData);
        setFormData(current => ({...current, ...parsedData}));
      } else {
        console.log('游댌 No hay datos para recuperar o estamos en modo edici칩n');
      }

      if (savedStep) {
        const step = parseInt(savedStep, 10);
        console.log('游댌 Paso guardado:', step);
        if (step > 0 && step <= 4) {
          console.log('游댌 Estableciendo currentStep a:', step);
          setCurrentStep(step);
        }
      } else {
        // Asegurarnos de empezar siempre desde el paso 1 si no hay paso guardado
        console.log('游댌 No hay paso guardado, estableciendo currentStep a 1');
        setCurrentStep(1);
      }
    } catch (err) {
      console.error('Error recovering form state:', err);
      // En caso de error, asegurarnos de empezar desde el paso 1
      setCurrentStep(1);
    }
  }, [initialData]);

  // Save current section and exit
  const handleSaveAndExit = async () => {
    try {
      // Log the saving attempt
      console.log(`Saving client data at step ${currentStep} and exiting...`);
      console.log('Form data being saved:', formData);

      // Show saving indicator
      setSectionSubmitting(true);

      if (onSavePartial) {
        await onSavePartial(formData, currentStep);
        console.log(`Successfully saved data for section ${currentStep}`);
      } else {
        // Fallback to full save if partial saving not provided
        await onSubmit(formData, documents);
        console.log('Successfully saved all client data');
      }

      // Clear local storage after successful save
      localStorage.removeItem('clientFormData');
      localStorage.removeItem('clientFormStep');

      // Navigate back to clients list
      navigate('/clients');
    } catch (error: any) {
      console.error(`Error saving client data at step ${currentStep}:`, error);
      alert(`Error al guardar: ${error.message || 'Error desconocido'}`);
    } finally {
      setSectionSubmitting(false);
    }
  };

  // Save current section and continue to next
  const handleSaveAndContinue = async () => {
    if (currentStep >= 4) return;

    try {
      // Log the saving attempt
      console.log(`Saving client data at step ${currentStep} and continuing...`);

      // Validate current step
      if (!validateCurrentStep()) {
        console.log('Validation failed for current step');
        return;
      }

      // Show saving indicator
      setSectionSubmitting(true);

      if (onSavePartial) {
        await onSavePartial(formData, currentStep);
        console.log(`Successfully saved data for section ${currentStep}`);
      }

      // Save current state to localStorage
      try {
        localStorage.setItem('clientFormData', JSON.stringify(formData));
        localStorage.setItem('clientFormStep', String(currentStep + 1));
        console.log('游댌 Guardado en localStorage - pr칩ximo paso:', currentStep + 1);
      } catch (err) {
        console.error('Error saving form state:', err);
      }

      // Continue to next step
      setCurrentStep(prev => prev + 1);
      console.log('游댌 Avanzando al paso:', currentStep + 1);

    } catch (error: any) {
      console.error(`Error saving client data at step ${currentStep}:`, error);
      alert(`Error al guardar: ${error.message || 'Error desconocido'}`);
    } finally {
      setSectionSubmitting(false);
    }
  };

  // Retroceder al paso anterior con m치s logs
  const prevStep = () => {
    console.log('游댌 Intentando retroceder del paso', currentStep);
    if (currentStep > 1) {
      // Guardar estado actual en localStorage con el paso anterior
      try {
        localStorage.setItem('clientFormData', JSON.stringify(formData));
        localStorage.setItem('clientFormStep', String(currentStep - 1));
        console.log('游댌 Actualizando localStorage con paso anterior:', currentStep - 1);
      } catch (err) {
        console.error('Error guardando estado del formulario:', err);
      }

      // Establecer el paso anterior
      setCurrentStep(prev => {
        const newStep = prev - 1;
        console.log('游댌 Estableciendo nuevo paso:', newStep);
        return newStep;
      });
    } else {
      console.log('游댌 Ya estamos en el primer paso, no se puede retroceder m치s');
    }
  };

  // Add a state for section saving
  const [sectionSubmitting, setSectionSubmitting] = useState(false);

  // Renderizado condicional de pasos
  const renderStep = () => {
    console.log('游댌 ClientForm: Renderizando paso', currentStep);
    console.log('Empresas disponibles:', companies.length);

    switch (currentStep) {
      case 1:
