import React, { useState, useEffect } from 'react';
import { executeQuery } from '../../services/mcpService';

interface Company {
  id: string;
  name: string;
}

interface CompanySelectorProps {
  value: string;
  onChange: (value: string) => void;
  className?: string;
}

const CompanySelector: React.FC<CompanySelectorProps> = ({ value, onChange, className }) => {
  const [companies, setCompanies] = useState<Company[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    console.log('üîç CompanySelector: Inicializando componente');

    const fetchCompanies = async () => {
      console.log('üîç CompanySelector: Empezando a obtener empresas del servidor MCP');
      try {
        setIsLoading(true);
        const response = await executeQuery('SELECT id, name FROM companies ORDER BY name ASC');
        console.log('üîç CompanySelector: Respuesta recibida del servidor MCP:', response);

        if (response && response.data) {
          console.log('üîç CompanySelector: Empresas obtenidas correctamente:', response.data);
          setCompanies(response.data);
        } else {
          console.error('üîç CompanySelector: No se encontraron datos en la respuesta');
          setError('No se pudieron cargar las empresas');
        }
      } catch (err: any) {
        console.error('üîç CompanySelector: Error obteniendo empresas:', err);
        setError(err.message || 'Error al cargar las empresas');
      } finally {
        setIsLoading(false);
      }
    };

    fetchCompanies();
  }, []);

  console.log('üîç CompanySelector: Renderizando componente con', companies.length, 'empresas');

  return (
    <div className="form-control w-full">
      <label className="label">
        <span className="label-text">Empresa <span className="text-error">*</span></span>
      </label>
      <select
        value={value || ''}
        onChange={(e) => onChange(e.target.value)}
        className={`select select-bordered w-full ${className || ''}`}
        disabled={isLoading}
      >
        <option value="">Seleccionar empresa</option>
        {companies.map((company) => (
          <option key={company.id} value={company.id}>
            {company.name}
          </option>
        ))}
      </select>
      {error && <span className="text-error text-sm mt-1">{error}</span>}
      {isLoading && <span className="text-sm mt-1">Cargando empresas...</span>}
    </div>
  );
};

export default CompanySelector;
