import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Client } from '../../services/clientService';
import DocumentUploader from '../documents/DocumentUploader';

// Interfaz para documentos
interface ClientDocument {
  id?: string;
  file: File;
  category: string;
  name: string;
}

// Props para el componente
interface ClientFormProps {
  initialData?: Partial<Client>;
  onSubmit: (data: Partial<Client>, documents: ClientDocument[]) => Promise<void>;
  isSubmitting: boolean;
}

// Categorías de documentos predefinidas
const DOCUMENT_CATEGORIES = [
  { value: 'identificacion', label: 'Identificación Oficial' },
  { value: 'comprobante_domicilio', label: 'Comprobante de Domicilio' },
  { value: 'comprobante_ingresos', label: 'Comprobante de Ingresos' },
  { value: 'estados_cuenta', label: 'Estados de Cuenta Bancarios' },
  { value: 'rfc', label: 'Constancia de Situación Fiscal (RFC)' },
  { value: 'curp', label: 'CURP' },
  { value: 'otros', label: 'Otros Documentos' },
];

// Tipos de empleo
const EMPLOYMENT_TYPES = [
  { value: 'empleado', label: 'Empleado' },
  { value: 'autonomo', label: 'Autónomo / Freelance' },
  { value: 'empresario', label: 'Empresario' },
  { value: 'jubilado', label: 'Jubilado' },
  { value: 'desempleado', label: 'Desempleado' },
  { value: 'estudiante', label: 'Estudiante' },
  { value: 'otro', label: 'Otro' },
];

// Estados civiles
const MARITAL_STATUS_TYPES = [
  { value: 'soltero', label: 'Soltero/a' },
  { value: 'casado', label: 'Casado/a' },
  { value: 'divorciado', label: 'Divorciado/a' },
  { value: 'viudo', label: 'Viudo/a' },
  { value: 'union_libre', label: 'Unión Libre' },
];

// Tipos de cuenta bancaria
const BANK_ACCOUNT_TYPES = [
  { value: 'nomina', label: 'Nómina' },
  { value: 'ahorro', label: 'Ahorro' },
  { value: 'cheques', label: 'Cheques' },
  { value: 'inversiones', label: 'Inversiones' },
];

const ClientForm: React.FC<ClientFormProps> = ({ initialData = {}, onSubmit, isSubmitting }) => {
  const navigate = useNavigate();

  // Estado para los datos del formulario
  const [formData, setFormData] = useState<Partial<Client>>({
    name: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    state: '',
    postal_code: '',
    birth_date: '',
    gender: '',
    marital_status: '',
    employment_type: '',
    employment_years: undefined,
    rfc: '',
    curp: '',
    monthly_income: undefined,
    additional_income: undefined,
    monthly_expenses: undefined,
    other_loan_balances: undefined,
    bank_name: '',
    bank_clabe: '',
    bank_account_number: '',
    bank_account_type: '',
    bank_account_origin: '',
    ...initialData,
  });

  // Estado para errores de validación
  const [errors, setErrors] = useState<Record<string, string>>({});

  // Estado para documentos
  const [documents, setDocuments] = useState<ClientDocument[]>([]);

  // Estado para el paso actual del formulario
  const [currentStep, setCurrentStep] = useState(1);

  // Calcular el progreso de llenado de formulario
  const calculateProgress = () => {
    let filledFields = 0;
    let totalFields = 0;

    // Paso 1: Datos Personales Básicos
    const step1Fields = ['name', 'email', 'phone', 'rfc', 'curp', 'birth_date', 'gender', 'marital_status', 'address', 'city', 'state', 'postal_code'];
    totalFields += step1Fields.length;
    filledFields += step1Fields.filter(field => formData[field as keyof Partial<Client>]).length;

    // Paso 2: Detalles Financieros
    const step2Fields = ['employment_type', 'employment_years', 'monthly_income', 'additional_income', 'monthly_expenses', 'other_loan_balances'];
    totalFields += step2Fields.length;
    filledFields += step2Fields.filter(field => formData[field as keyof Partial<Client>]).length;

    // Paso 3: Datos Bancarios
    const step3Fields = ['bank_name', 'bank_clabe', 'bank_account_number', 'bank_account_type', 'bank_account_origin'];
    totalFields += step3Fields.length;
    filledFields += step3Fields.filter(field => formData[field as keyof Partial<Client>]).length;

    // Calcular porcentaje
    return Math.round((filledFields / totalFields) * 100);
  };

  // Manejar cambios en el formulario
  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value, type } = e.target as HTMLInputElement;

    // Convertir a número si es un campo numérico
    if (type === 'number') {
      setFormData(prev => ({
        ...prev,
        [name]: value === '' ? undefined : parseFloat(value)
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }

    // Limpiar error si existe
    if (errors[name]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[name];
        return newErrors;
      });
    }
  };

  // Manejar documentos agregados
  const handleDocumentAdded = (document: ClientDocument) => {
    setDocuments(prev => [...prev, document]);
  };

  // Manejar documentos eliminados
  const handleDocumentRemoved = (index: number) => {
    setDocuments(prev => prev.filter((_, i) => i !== index));
  };

  // Validar datos antes de enviar
  const validateForm = () => {
    const newErrors: Record<string, string> = {};

    // Validar campos obligatorios
    if (!formData.name?.trim()) {
      newErrors.name = 'El nombre es obligatorio';
    }

    // Validar formato de email
    if (formData.email && !/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'El correo electrónico no es válido';
    }

    // Validar RFC
    if (formData.rfc && !/^([A-ZÑ&]{3,4})(\d{6})([A-Z\d]{3})$/.test(formData.rfc.toUpperCase())) {
      newErrors.rfc = 'El RFC no tiene un formato válido';
    }

    // Validar CURP
    if (formData.curp && !/^([A-Z][AEIOUX][A-Z]{2}\d{2}(?:0[1-9]|1[0-2])(?:0[1-9]|[12]\d|3[01])[HM](?:AS|B[CS]|C[CLMSH]|D[FG]|G[TR]|HG|JC|M[CNS]|N[ETL]|OC|PL|Q[TR]|S[PLR]|T[CSL]|VZ|YN|ZS)[B-DF-HJ-NP-TV-Z]{3}[A-Z\d])(\d)$/.test(formData.curp.toUpperCase())) {
      newErrors.curp = 'La CURP no tiene un formato válido';
    }

    // Validar CLABE
    if (formData.bank_clabe && !/^\d{18}$/.test(formData.bank_clabe)) {
      newErrors.bank_clabe = 'La CLABE debe tener 18 dígitos';
    }

    // Establecer errores
    setErrors(newErrors);

    // Devolver si es válido
    return Object.keys(newErrors).length === 0;
  };

  // Manejar envío del formulario
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (validateForm()) {
      try {
        await onSubmit(formData, documents);
        navigate('/clients');
      } catch (error) {
        console.error('Error al guardar cliente:', error);
      }
    }
  };

  // Avanzar al siguiente paso
  const nextStep = () => {
    if (currentStep < 4) {
      setCurrentStep(prev => prev + 1);
    }
  };

  // Retroceder al paso anterior
  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(prev => prev - 1);
    }
  };

  // Renderizado condicional de pasos
  const renderStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <div className="space-y-6">
            <h3 className="text-lg font-semibold">Datos Personales Básicos</h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Nombre Completo*</span>
                </label>
                <input
                  type="text"
                  name="name"
                  value={formData.name || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.name ? 'input-error' : ''}`}
                  placeholder="Nombre completo"
                />
                {errors.name && <span className="text-error text-xs mt-1">{errors.name}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Correo Electrónico</span>
                </label>
                <input
                  type="email"
                  name="email"
                  value={formData.email || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.email ? 'input-error' : ''}`}
                  placeholder="correo@ejemplo.com"
                />
                {errors.email && <span className="text-error text-xs mt-1">{errors.email}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Teléfono</span>
                </label>
                <input
                  type="tel"
                  name="phone"
                  value={formData.phone || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.phone ? 'input-error' : ''}`}
                  placeholder="(XXX) XXX-XXXX"
                />
                {errors.phone && <span className="text-error text-xs mt-1">{errors.phone}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Fecha de Nacimiento</span>
                </label>
                <input
                  type="date"
                  name="birth_date"
                  value={formData.birth_date || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.birth_date ? 'input-error' : ''}`}
                />
                {errors.birth_date && <span className="text-error text-xs mt-1">{errors.birth_date}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Género</span>
                </label>
                <select
                  name="gender"
                  value={formData.gender || ''}
                  onChange={handleChange}
                  className={`select select-bordered w-full ${errors.gender ? 'select-error' : ''}`}
                >
                  <option value="">Seleccionar género</option>
                  <option value="masculino">Masculino</option>
                  <option value="femenino">Femenino</option>
                  <option value="otro">Otro</option>
                </select>
                {errors.gender && <span className="text-error text-xs mt-1">{errors.gender}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Estado Civil</span>
                </label>
                <select
                  name="marital_status"
                  value={formData.marital_status || ''}
                  onChange={handleChange}
                  className={`select select-bordered w-full ${errors.marital_status ? 'select-error' : ''}`}
                >
                  <option value="">Seleccionar estado civil</option>
                  {MARITAL_STATUS_TYPES.map(option => (
                    <option key={option.value} value={option.value}>{option.label}</option>
                  ))}
                </select>
                {errors.marital_status && <span className="text-error text-xs mt-1">{errors.marital_status}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">RFC</span>
                </label>
                <input
                  type="text"
                  name="rfc"
                  value={formData.rfc || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.rfc ? 'input-error' : ''}`}
                  placeholder="RFC"
                />
                {errors.rfc && <span className="text-error text-xs mt-1">{errors.rfc}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">CURP</span>
                </label>
                <input
                  type="text"
                  name="curp"
                  value={formData.curp || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.curp ? 'input-error' : ''}`}
                  placeholder="CURP"
                />
                {errors.curp && <span className="text-error text-xs mt-1">{errors.curp}</span>}
              </div>
            </div>

            <div className="form-control w-full">
              <label className="label">
                <span className="label-text">Dirección</span>
              </label>
              <input
                type="text"
                name="address"
                value={formData.address || ''}
                onChange={handleChange}
                className={`input input-bordered w-full ${errors.address ? 'input-error' : ''}`}
                placeholder="Calle, número, colonia"
              />
              {errors.address && <span className="text-error text-xs mt-1">{errors.address}</span>}
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Ciudad</span>
                </label>
                <input
                  type="text"
                  name="city"
                  value={formData.city || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.city ? 'input-error' : ''}`}
                  placeholder="Ciudad"
                />
                {errors.city && <span className="text-error text-xs mt-1">{errors.city}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Estado</span>
                </label>
                <input
                  type="text"
                  name="state"
                  value={formData.state || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.state ? 'input-error' : ''}`}
                  placeholder="Estado"
                />
                {errors.state && <span className="text-error text-xs mt-1">{errors.state}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Código Postal</span>
                </label>
                <input
                  type="text"
                  name="postal_code"
                  value={formData.postal_code || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.postal_code ? 'input-error' : ''}`}
                  placeholder="Código Postal"
                />
                {errors.postal_code && <span className="text-error text-xs mt-1">{errors.postal_code}</span>}
              </div>
            </div>
          </div>
        );

      case 2:
        return (
          <div className="space-y-6">
            <h3 className="text-lg font-semibold">Detalles Financieros y Balance</h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Tipo de Empleo</span>
                </label>
                <select
                  name="employment_type"
                  value={formData.employment_type || ''}
                  onChange={handleChange}
                  className={`select select-bordered w-full ${errors.employment_type ? 'select-error' : ''}`}
                >
                  <option value="">Seleccionar tipo de empleo</option>
                  {EMPLOYMENT_TYPES.map(option => (
                    <option key={option.value} value={option.value}>{option.label}</option>
                  ))}
                </select>
                {errors.employment_type && <span className="text-error text-xs mt-1">{errors.employment_type}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Antigüedad (años)</span>
                </label>
                <input
                  type="number"
                  name="employment_years"
                  value={formData.employment_years || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.employment_years ? 'input-error' : ''}`}
                  placeholder="Años de antigüedad"
                  min="0"
                />
                {errors.employment_years && <span className="text-error text-xs mt-1">{errors.employment_years}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Ingreso Mensual ($)</span>
                </label>
                <input
                  type="number"
                  name="monthly_income"
                  value={formData.monthly_income || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.monthly_income ? 'input-error' : ''}`}
                  placeholder="0.00"
                  min="0"
                  step="0.01"
                />
                {errors.monthly_income && <span className="text-error text-xs mt-1">{errors.monthly_income}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Ingresos Adicionales ($)</span>
                </label>
                <input
                  type="number"
                  name="additional_income"
                  value={formData.additional_income || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.additional_income ? 'input-error' : ''}`}
                  placeholder="0.00"
                  min="0"
                  step="0.01"
                />
                {errors.additional_income && <span className="text-error text-xs mt-1">{errors.additional_income}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Gastos Mensuales ($)</span>
                </label>
                <input
                  type="number"
                  name="monthly_expenses"
                  value={formData.monthly_expenses || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.monthly_expenses ? 'input-error' : ''}`}
                  placeholder="0.00"
                  min="0"
                  step="0.01"
                />
                {errors.monthly_expenses && <span className="text-error text-xs mt-1">{errors.monthly_expenses}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Saldos de Otros Créditos ($)</span>
                </label>
                <input
                  type="number"
                  name="other_loan_balances"
                  value={formData.other_loan_balances || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.other_loan_balances ? 'input-error' : ''}`}
                  placeholder="0.00"
                  min="0"
                  step="0.01"
                />
                {errors.other_loan_balances && <span className="text-error text-xs mt-1">{errors.other_loan_balances}</span>}
              </div>
            </div>
          </div>
        );

      case 3:
        return (
          <div className="space-y-6">
            <h3 className="text-lg font-semibold">Datos de la Cuenta Bancaria</h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Banco</span>
                </label>
                <input
                  type="text"
                  name="bank_name"
                  value={formData.bank_name || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.bank_name ? 'input-error' : ''}`}
                  placeholder="Nombre del banco"
                />
                {errors.bank_name && <span className="text-error text-xs mt-1">{errors.bank_name}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">CLABE</span>
                </label>
                <input
                  type="text"
                  name="bank_clabe"
                  value={formData.bank_clabe || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.bank_clabe ? 'input-error' : ''}`}
                  placeholder="18 dígitos"
                />
                {errors.bank_clabe && <span className="text-error text-xs mt-1">{errors.bank_clabe}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Número de Cuenta</span>
                </label>
                <input
                  type="text"
                  name="bank_account_number"
                  value={formData.bank_account_number || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.bank_account_number ? 'input-error' : ''}`}
                  placeholder="Número de cuenta"
                />
                {errors.bank_account_number && <span className="text-error text-xs mt-1">{errors.bank_account_number}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Tipo de Cuenta</span>
                </label>
                <select
                  name="bank_account_type"
                  value={formData.bank_account_type || ''}
                  onChange={handleChange}
                  className={`select select-bordered w-full ${errors.bank_account_type ? 'select-error' : ''}`}
                >
                  <option value="">Seleccionar tipo de cuenta</option>
                  {BANK_ACCOUNT_TYPES.map(option => (
                    <option key={option.value} value={option.value}>{option.label}</option>
                  ))}
                </select>
                {errors.bank_account_type && <span className="text-error text-xs mt-1">{errors.bank_account_type}</span>}
              </div>

              <div className="form-control w-full">
                <label className="label">
                  <span className="label-text">Origen de la Cuenta (Estado)</span>
                </label>
                <input
                  type="text"
                  name="bank_account_origin"
                  value={formData.bank_account_origin || ''}
                  onChange={handleChange}
                  className={`input input-bordered w-full ${errors.bank_account_origin ? 'input-error' : ''}`}
                  placeholder="Estado donde se abrió la cuenta"
                />
                {errors.bank_account_origin && <span className="text-error text-xs mt-1">{errors.bank_account_origin}</span>}
              </div>
            </div>
          </div>
        );

      case 4:
        return (
          <div className="space-y-6">
            <h3 className="text-lg font-semibold">Documentos</h3>
            <p className="text-sm text-gray-500">Sube los documentos del cliente. Todos los formatos son aceptados.</p>

            <DocumentUploader 
              categories={DOCUMENT_CATEGORIES}
              onDocumentAdded={handleDocumentAdded}
              onDocumentRemoved={handleDocumentRemoved}
              existingDocuments={documents}
              clientId={initialData?.id}
            />
          </div>
        );

      default:
        return null;
    }
  };

  // Calcular y formatear el progreso
  const progress = calculateProgress();

  return (
    <div className="card bg-base-100 shadow-xl">
      <div className="card-body">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-6">
          <h2 className="card-title text-xl">
            {initialData?.id ? 'Editar Cliente' : 'Nuevo Cliente'}
          </h2>
          <div className="flex items-center mt-2 sm:mt-0">
            <span className="text-sm mr-2">{progress}% completado</span>
            <progress className="progress progress-primary w-40" value={progress} max="100"></progress>
          </div>
        </div>

        <div className="mb-6">
          <ul className="steps steps-horizontal w-full">
            <li className={`step ${currentStep >= 1 ? 'step-primary' : ''}`}>Datos Personales</li>
            <li className={`step ${currentStep >= 2 ? 'step-primary' : ''}`}>Financieros</li>
            <li className={`step ${currentStep >= 3 ? 'step-primary' : ''}`}>Datos Bancarios</li>
            <li className={`step ${currentStep >= 4 ? 'step-primary' : ''}`}>Documentos</li>
          </ul>
        </div>

        <form onSubmit={handleSubmit}>
          {renderStep()}

          <div className="flex justify-between mt-6">
            <div>
              {currentStep > 1 && (
                <button 
                  type="button" 
                  className="btn btn-outline"
                  onClick={prevStep}
                >
                  Anterior
                </button>
              )}
            </div>

            <div className="flex gap-2">
              <button 
                type="button" 
                className="btn btn-outline"
                onClick={() => navigate('/clients')}
              >
                Cancelar
              </button>

              {currentStep < 4 ? (
                <button 
                  type="button" 
                  className="btn btn-primary"
                  onClick={nextStep}
                >
                  Siguiente
                </button>
              ) : (
                <button 
                  type="submit" 
                  className={`btn btn-primary ${isSubmitting ? 'loading' : ''}`}
                  disabled={isSubmitting}
                >
                  {initialData?.id ? 'Actualizar' : 'Guardar'} Cliente
                </button>
              )}
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ClientForm;
