import { supabase } from '../lib/supabaseClient';
import { TABLES } from '../utils/constants/tables';
import { uploadDocument, DocumentUpload } from './documentService';

export interface Client {
  id: string;
  created_at: string;
  name?: string;
  first_name?: string;
  paternal_surname?: string;
  maternal_surname?: string;
  email: string;
  phone: string;
  address?: string;
  city?: string;
  state?: string;
  postal_code?: string;
  birth_date?: string;
  gender?: string;
  marital_status?: string;
  employment_type?: string;
  employment_years?: number;
  rfc?: string;
  curp?: string;
  monthly_income?: number;
  additional_income?: number;
  monthly_expenses?: number;
  other_loan_balances?: number;
  bank_name?: string;
  bank_clabe?: string;
  bank_account_number?: string;
  bank_account_type?: string;
  bank_account_origin?: string;
  company_id?: string;
  advisor_id?: string;
}

export interface ClientDocument {
  id?: string;
  file: File;
  category: string;
  name: string;
}

export interface ClientFilter {
  searchQuery?: string;
  advisor_id?: string;
  company_id?: string;
  dateFrom?: string;
  dateTo?: string;
  page?: number;
  pageSize?: number;
}

const USERS_TABLE = 'users';

const mapUserToClient = (userData: any): Client => {
  if (!userData) {
    console.error('Error: userData is undefined in mapUserToClient');
    throw new Error('No se pudo procesar la información del cliente, los datos son inválidos');
  }

  const fullName = userData.name || [
    userData.first_name,
    userData.paternal_surname,
    userData.maternal_surname
  ].filter(Boolean).join(' ');

  return {
    id: userData.id,
    created_at: userData.created_at,
    name: fullName,
    first_name: userData.first_name,
    paternal_surname: userData.paternal_surname,
    maternal_surname: userData.maternal_surname,
    email: userData.email,
    phone: userData.phone,
    birth_date: userData.birth_date,
    company_id: userData.company_id,
    rfc: userData.rfc,
    curp: userData.curp,
    advisor_id: userData.advisor_id,
    address: userData.address,
    city: userData.city,
    state: userData.state,
    postal_code: userData.postal_code,
    gender: userData.gender,
    marital_status: userData.marital_status,
    employment_type: userData.employment_type,
    employment_years: userData.employment_years,
    monthly_income: userData.monthly_income,
    additional_income: userData.additional_income,
    monthly_expenses: userData.monthly_expenses,
    other_loan_balances: userData.other_loan_balances,
    bank_name: userData.bank_name,
    bank_clabe: userData.bank_clabe,
    bank_account_number: userData.bank_account_number,
    bank_account_type: userData.bank_account_type,
    bank_account_origin: userData.bank_account_origin,
  };
};

export const getClients = async (filters?: ClientFilter) => {
  try {
    let query = supabase.from(USERS_TABLE)
      .select('id, email, first_name, paternal_surname, maternal_surname, phone, company_id, created_at, birth_date, rfc, curp, advisor_id, address, city, state, postal_code, gender, marital_status, employment_type, employment_years, monthly_income, additional_income, monthly_expenses, other_loan_balances, bank_name, bank_clabe, bank_account_number, bank_account_type, bank_account_origin', { count: 'exact' });

    if (filters) {
      if (filters.advisor_id) {
        query = query.eq('advisor_id', filters.advisor_id);
      }

      if (filters.company_id) {
        query = query.eq('company_id', filters.company_id);
      }

      if (filters.dateFrom) {
        query = query.gte('created_at', filters.dateFrom);
      }

      if (filters.dateTo) {
        query = query.lte('created_at', filters.dateTo);
      }

      if (filters.searchQuery) {
        query = query.or(
          `first_name.ilike.%${filters.searchQuery}%,paternal_surname.ilike.%${filters.searchQuery}%,maternal_surname.ilike.%${filters.searchQuery}%,email.ilike.%${filters.searchQuery}%,phone.ilike.%${filters.searchQuery}%,rfc.ilike.%${filters.searchQuery}%,curp.ilike.%${filters.searchQuery}%`
        );
      }

      if (filters.page !== undefined && filters.pageSize) {
        const from = filters.page * filters.pageSize;
        const to = from + filters.pageSize - 1;
        query = query.range(from, to);
      }
    }

    query = query.order('created_at', { ascending: false });

    const { data, error, count } = await query;

    if (error) {
      console.error('Error fetching users:', error);
      throw error;
    }

    const clients = data ? data.map(mapUserToClient) : [];

    return {
      clients,
      totalCount: count || 0
    };
  } catch (error) {
    console.error('Error in getClients:', error);
    throw error;
  }
};

export const getClientById = async (id: string) => {
  try {
    const { data, error } = await supabase
      .from(USERS_TABLE)
      .select('id, email, first_name, paternal_surname, maternal_surname, phone, company_id, created_at, birth_date, rfc, curp, advisor_id, address, city, state, postal_code, gender, marital_status, employment_type, employment_years, monthly_income, additional_income, monthly_expenses, other_loan_balances, bank_name, bank_clabe, bank_account_number, bank_account_type, bank_account_origin')
      .eq('id', id)
      .single();

    if (error) {
      console.error(`Error fetching user with ID ${id}:`, error);
      throw error;
    }

    if (!data) {
      console.error(`No data found for client with ID ${id}`);
      throw new Error(`No se encontró cliente con ID: ${id}`);
    }

    return mapUserToClient(data);
  } catch (error) {
    console.error(`Error in getClientById:`, error);
    throw error;
  }
};

function escapeSQLString(str: string) {
  if (!str) return '';
  return str.replace(/'/g, "''");
}

export const getClientApplications = async (clientId: string) => {
  try {
    const client = await getClientById(clientId).catch(err => {
      throw err;
    });

    if (!client) {
      console.warn('Client not found. Returning empty applications list.');
      return [];
    }

    const query = `
      SELECT * FROM ${TABLES.APPLICATIONS}
      WHERE client_name = '${escapeSQLString(client.name || '')}'
      ORDER BY created_at DESC
    `;

    const executeQuery = async (query: string) => {
      try {
        const response = await fetch('http://localhost:3100/query', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ query: query }),
        });

        const result = await response.json();

        if (result.error) {
          console.error('Error en la consulta SQL:', result.error);
          throw new Error(result.error);
        }

        return result.data;
      } catch (error) {
        console.error('Error ejecutando la consulta:', error);
        throw error;
      }
    };

    const data = await executeQuery(query);
    return data;
  } catch (error) {
    console.error(`Error fetching applications for client ${clientId}:`, error);
    throw error;
  }
};

export const uploadClientDocuments = async (
  clientId: string, 
  userId: string, 
  documents: ClientDocument[]
) => {
  const uploadPromises = documents.map(doc => {
    const upload: DocumentUpload = {
      file: doc.file,
      client_id: clientId,
      userId: userId,
      documentName: doc.name,
      category: doc.category
    };

    return uploadDocument(upload);
  });

  try {
    return await Promise.all(uploadPromises);
  } catch (error) {
    console.error('Error uploading client documents:', error);
    throw error;
  }
};

