    const updatedClient = mapUserToClient(data[0]);

    if (documents && documents.length > 0 && userId) {
      await uploadClientDocuments(id, userId, documents);
    }

    return updatedClient;
  } catch (error) {
    console.error(`Error in updateClient:`, error);
    throw error;
  }
};

export const deleteClient = async (id: string) => {
  try {
    const { error } = await supabase
      .from(USERS_TABLE)
      .delete()
      .eq('id', id);

    if (error) {
      console.error(`Error deleting user with ID ${id}:`, error);
      throw error;
    }

    return true;
  } catch (error) {
    console.error(`Error in deleteClient:`, error);
    throw error;
  }
};

export const checkClientExists = async (email: string, rfc?: string) => {
  try {
    let query = supabase
      .from(USERS_TABLE)
      .select('id, email, rfc')
      .eq('email', email);

    if (rfc) {
      query = query.or(`rfc.eq.${rfc}`);
    }

    const { data, error } = await query;

    if (error) {
      console.error('Error checking client existence:', error);
      throw error;
    }

    return data.length > 0 ? data[0] : null;
  } catch (error) {
    console.error('Error in checkClientExists:', error);
    throw error;
  }
};

export const getClientCount = async (filters?: ClientFilter) => {
  try {
    let query = supabase
      .from(USERS_TABLE)
      .select('id', { count: 'exact', head: true });

    if (filters) {
      if (filters.advisor_id) {
        query = query.eq('advisor_id', filters.advisor_id);
      }

      if (filters.company_id) {
        query = query.eq('company_id', filters.company_id);
      }

      if (filters.dateFrom) {
        query = query.gte('created_at', filters.dateFrom);
      }

      if (filters.dateTo) {
        query = query.lte('created_at', filters.dateTo);
      }

      if (filters.searchQuery) {
        query = query.or(
          `first_name.ilike.%${filters.searchQuery}%,paternal_surname.ilike.%${filters.searchQuery}%,maternal_surname.ilike.%${filters.searchQuery}%,email.ilike.%${filters.searchQuery}%,phone.ilike.%${filters.searchQuery}%,rfc.ilike.%${filters.searchQuery}%,curp.ilike.%${filters.searchQuery}%`
        );
      }
    }

    const { count, error } = await query;

    if (error) {
      console.error('Error getting client count:', error);
      throw error;
    }

    return count || 0;
  } catch (error) {
    console.error('Error in getClientCount:', error);
    return 0;
  }
};
